ghost function f(n: int): int {
  if n < 0 then 0 else 3*f(n-5) + n
}
method problem5(n:nat) returns (x: int)
ensures x == f(n)
{
    var a := 1;
    var b := 0;
    var k := n;
    while k >= 0
    invariant f(n) == a*f(k) + b
    invariant -5 <= k <= n
    decreases k
    {
        assert f(n) == a*f(k) + b;
        assert f(n) == a*(3*f(k-5)+k) + b;
        assert f(n) == 3*a*f(k-5) + a*k + b;
        b := a*k + b;
        assert f(n) == 3*a*f(k-5) + b;
        a := 3*a;
        assert f(n) == a*f(k-5) + b;
        k := k - 5;
        assert f(n) == a*f(k) + b;
    }
    assert k < 0;
    assert f(n) == a*f(k) + b;
    assert f(n) == a*0 + b;
    x := b;
    assert x== f(n);
}

function Fibonacci(n: nat): nat {
  match n {
    case 0 => 0
    case 1 => 1
    case _ => Fibonacci(n - 1) + Fibonacci(n - 2)
  }
}
method FibonacciIterative(n: nat) returns (f: nat)
  ensures f == Fibonacci(n)
{
  if n < 2 {
    return n;
  }
  var prev := 1;
  f := 1;
  var i := 2;
  while i < n
    invariant i <= n
    invariant prev == Fibonacci(i - 1)
    invariant f == Fibonacci(i)
  {
    prev, f := f, f + prev;
    i := i + 1;
  }
}

method g30(m: nat, n: nat) returns (PROD: nat)
  ensures PROD == m * n
{
  var M: nat;
  M := m;
  PROD := 0;
  while M > 0 
    decreases M
    invariant PROD == n * (m - M)
  {
    PROD := PROD + n;
    M := M - 1;
  }
}

function fib(n : nat ) : nat
  decreases n
{
    if n < 2 then n else fib(n - 2) + fib(n - 1)
}
method computeFib (n : nat) returns (x : nat)
requires n >= 0
ensures x == fib(n)
{
    var i := 0;
    x := 0;
    var y := 1;
    while  i < n 
	decreases n-i
	invariant 0 <= i <= n && x == fib(i) && y == fib(i+1)
    {
        x, y := y, x + y; 
        i := i + 1;
    }
}

