method remove(items: array<int>, index: nat) returns (r: array<int>)
  requires items != null && index < items.Length
  requires items.Length > 0
  ensures r != null && r.Length == items.Length - 1
  ensures forall k: nat :: k < index ==> r[k] == items[k]
  ensures forall k :: index <= k < r.Length ==> r[k] == items[k + 1]
{
    var newlen := items.Length - 1;
    r := new int[newlen];
    var i: nat := 0;
    while i < index
      invariant i <= index
      decreases index - i
      invariant r.Length == newlen
      invariant forall k: nat :: k < i
                ==> (k < index ==> r[k] == items[k])
    {
        r[i] := items[i];
        i := i + 1;
    }
    assert i == index;
    while i < newlen
      invariant index <= i <= newlen
      decreases newlen - i
      invariant r.Length == newlen
      invariant forall k: nat :: k < index ==> r[k] == items[k]
      invariant forall k :: index <= k < i ==> r[k] == items[k + 1]
    {
        r[i] := items[i + 1];
        i := i + 1;
    }
}

method isPrefix(pre: string, str: string) returns (res: bool){
  if(|pre| == 0){
    return true;
  }
  if (|pre| > |str|){
    return false;
  }
  var i: nat := 0;
  var n:= |pre|;
  while(i < n)
    decreases n - i
    invariant 0 <= i <= n
  {
    if(pre[i] != str[i]){
      return false;
    }
    i := i + 1;
  }
  return true;
}
method isSubstring(sub: string, str: string) returns (res: bool){
  if(|sub| == 0){
    return true;
  }
  if(|str| < |sub|){
    return false;
  }
  var i: nat := 0;
  var substr: string;
  var isPref : bool;
  var len: int := |str| - |sub|;
  while (i < len)
    decreases len - i
    invariant 0 <= i <= len
  {
    substr := str[i..];
    isPref := isPrefix(sub, substr);
    if(isPref){
      return true;
    }
    i := i + 1; 
  }
  return false;
}
method haveCommonKSubstring(k: nat, str1: string, str2:string) returns (res: bool){
  if (k == 0){
    return true;
  }
  if (k > |str1| || k > |str2|){
    return false;
  }
  var i: nat := 0;
  while(i < |str1| - k)
    decreases |str1| - k - i 
    invariant 0 <= i <= |str1| - k
  {
    var substr : string := str1[i..i+k-1];
    var isSubStr : bool := isSubstring(substr, str2);
    if(isSubStr){
      return true;
    }
    i := i + 1;
  }
  return false;
}
method maxCommonSubstringLength(str1: string, str2: string) returns (len:nat){
  var short : string := str1;
  var long : string := str2;
  if(|str1| > |str2|){
    short := str2;
    long := str1;
  }
  var k : nat := 0;
  var haveCommonSubstring : bool := true;
  while(k < |short|)
    decreases |short| - k
    invariant 0 <= k <= |short|
  {
    haveCommonSubstring := haveCommonKSubstring(k, short, long);
    if(!haveCommonSubstring){
      break;
    }
    k := k + 1;
  }
  return k;
}

