function IsEven(a : int) : bool
    requires a >= 0
{
    if a == 0 then      true 
    else if a == 1 then false 
    else                IsEven(a - 2)
}
lemma EvenSquare(a : int)
requires a >= 0
ensures IsEven(a) ==> IsEven(a * a)
{
    if a >= 2 && IsEven(a) {
        EvenSquare(a - 2);
        assert a * a == (a - 2) * (a - 2) + 4 * a - 4;
        EvenDouble(2 * a - 2);
        EvenPlus((a - 2) * (a - 2), 4 * a - 4);
    }
}
lemma EvenDouble(a: int)
    requires a >= 0
    ensures IsEven(a + a)
{
    if a >= 2 {
        EvenDouble(a - 2);
    }
}
