            decreases |a| - index
            invariant index <= |a|;
            invariant |b| >= 1;
            invariant b[|b| - 1] == last;
            invariant forall j, k::0 <= j < k < |b|  ==> b[j] < b[k];
            invariant last in a[..index];   
            invariant forall j :: j in a[..index] <==> j in b
        {
            if ( a[index] != last ) { 
                b := b + [a[index]];
                last := a[index];
            }
            index := index + 1;
        }
    }
}
