method MergeSort(a1:array<int>) returns (a:array<int>)
  requires a1 != null && a1.Length > 0;
  ensures a != null;
  ensures forall k:: forall l:: 0 <= k < l < a.Length ==> a[k] <= a[l];
{
  a := ms(a1, 0, a1.Length-1);
  return;
}
method ms(a1:array<int>, l:int, u:int) returns (a:array<int>)
  requires a1 != null;
  requires 0 <= l <= u < a1.Length;
  ensures a != null && a.Length == a1.Length;
  ensures forall elem1:: forall elem2:: l <= elem1 <= elem2 <= u ==> a[elem1] <= a[elem2];
  decreases u - l;
  decreases u;
{
  a := new int[a1.Length];
  assume forall k:: 0 <= k < a1.Length ==> a[k] == a1[k];
  if (l >= u)
  {
    return;
  }
  else
  {
    var m:int := (l + u) / 2;
    a := ms(a, l, m);
    a := ms(a, m+1, u);
    a := merge(a, l, m, u);
    return;
  }
}
method merge(a1:array<int>, l:int, m:int, u:int) returns (a:array<int>)
  requires a1 != null;
  requires 0 <= l <= m < u < a1.Length;
  ensures a != null && a.Length == a1.Length;
  ensures forall elem1:: forall elem2:: l <= elem1 <= elem2 <= u ==> a[elem1] <= a[elem2];
{
  a := new int[a1.Length];
  assume forall k:: 0 <= k < a1.Length ==> a[k] == a1[k];
  var buf := new int[u-l+1];
  var i:int := l;
  var j:int := m + 1;
  var k:int := 0;
  while (k < u-l+1)
	invariant m + 1 <= j <= a.Length;
	invariant l <= i <= m + 1;
	invariant k == i - l + j - m - 1;
	invariant i > m ==> j < a.Length || k > u-l;
	decreases u-l-k;
  {
    if (i > m)
    {
      buf[k] := a[j];
      j := j + 1;
    }
    else if (j > u)
    {
      buf[k] := a[i];
      i := i + 1;
    }
    else if (a[i] <= a[j])
    {
      buf[k] := a[i];
      i := i + 1;
    }
    else
    {
      buf[k] := a[j];
      j := j + 1;
    }
    k := k + 1;
  }
  assume k == buf.Length;
  assume forall elem1:: forall elem2:: 0 <= elem1 <= elem2 < k ==> buf[elem1] <= buf[elem2];
  k := 0;
  while (k < u-l+1)
	invariant 0 <= k <= u - l + 1 == buf.Length;
	invariant forall elem1:: l <= elem1 < k + l ==> a[elem1] == buf[elem1 - l]; 
	invariant forall elem1:: forall elem2:: 0 <= elem1 <= elem2 < buf.Length ==> buf[elem1] <= buf[elem2];
	invariant forall elem1:: forall elem2:: 0 <= elem1 <= elem2 < k ==> buf[elem1] <= buf[elem2]; 
	invariant forall elem1:: forall elem2:: l <= elem1 <= elem2 < k + l ==> a[elem1] <= a[elem2];
	decreases u-l-k;
  {
    a[l + k] := buf[k];
    k := k + 1;
  }
}

ghost function f(n: int): int {
  if n <= 0 then 1 else n + f(n-1)*f(n-2)
}
ghost function fSum(n: nat): int {
  if n <= 0 then 0 else f(n-1) + fSum(n-1)
}
method problem6(n:nat) returns (a: int)
ensures a == fSum(n)
{
  a := 0;
  var k := 0;
  var x := 1;
  var y := 2;
  while k < n
  invariant 0 <= k <= n && x == f(k) && y == f(k+1) && a == fSum(k)
  decreases n-k
  {
    assert x == f(k) && y == f(k+1) && a == fSum(k);
    k := k + 1;
    assert x == f(k-1) && y == f(k) && a == fSum(k-1);
    assert x == f(k-1) && y == f(k) && a == fSum(k) - f(k-1);
    a := a + x;
    assert x == f(k-1) && y == f(k) && a == fSum(k) - f(k-1) + f(k-1);
    assert x == f(k-1) && y == f(k) && a == fSum(k);
    x, y := y, k+1 + x*y;    
    assert x == f(k) && y == k+1+f(k-1)*f(k) && a == fSum(k);
    assert x == f(k) && y == k+1+f(k+1-2)*f(k+1-1) && a == fSum(k);
    assert x == f(k) && y == f(k+1) && a == fSum(k);
  }
  assert a == fSum(k);
}

module Sets {
  lemma {:opaque} ProperSubsetImpliesSmallerCardinality<T>(a: set<T>, b: set<T>)
    requires a < b
    ensures |a| < |b|
  {
    assert |b| == |a| + |b-a|;
  }
  lemma {:opaque} SetInclusionImpliesSmallerCardinality<T>(a: set<T>, b: set<T>)
    requires a <= b
    ensures |a| <= |b|
  {
    assert b == a + (b - a);
  }
  lemma {:opaque} SetInclusionImpliesStrictlySmallerCardinality<T>(a: set<T>, b: set<T>)
    requires a < b
    ensures |a| < |b|
  {
    assert b == a + (b - a);
  }
  lemma {:opaque} SetInclusionAndEqualCardinalityImpliesSetEquality<T>(a: set<T>, b: set<T>)
    requires a <= b
    requires |a| == |b|
    ensures a == b
  {
    assert b == a + (b - a);
  }
  function SetRange(n: int) : set<int>
  {
    set i | 0 <= i < n
  }
  lemma CardinalitySetRange(n: int)
  requires n >= 0
  ensures |SetRange(n)| == n
  {
    if n == 0 {
    } else {
      CardinalitySetRange(n-1);
      assert SetRange(n)
          == SetRange(n-1) + {n-1};
    }
  }
}

