 function log2(size:int) : (result:int)
    requires 1 <= size
    ensures 0 <= result
 {
     if (size == 1) then 0
     else 1 + log2(size/2)
 }
method binsearch(s: seq<int>, n: int) returns (found:bool, steps: int)
	requires sorted(s)
    requires 1 <= |s|
	ensures !found ==> !member(s, n)										
	ensures found ==> member(s, n)
{
    steps := 0;
    assert steps <= log2(|s|);
	var lo := 0;
	var hi := |s| - 1;
	var mid := 0;
    var size := |s|;
    var maxAttempts := log2(size);
	found := false;
	while(lo <= hi && !found)
		decreases hi-lo + (if found then 0 else 1)							
        decreases size
		invariant 0 <= lo <= |s| && -1 <= hi < |s| 
		invariant !member(s[..lo],n)										
		invariant !member(s[hi+1..],n)
		invariant found ==> member(s, n)						
	{
		mid := (hi + lo)/2;
		if (s[mid] == n) { 
			found := true;
		} else if (s[mid] < n) {
			lo := mid + 1;
		} else {
			hi := mid - 1;
		}
        steps := steps + 1;
	}
}
method binsearchInd(s: seq<int>, n:int) returns (ind:int)
	requires sorted(s)
	ensures ind < 0 ==> !member(s, n)										
	ensures 0 <= ind ==> (ind < |s| && s[ind] == n)							
{
	if |s| == 0 { return -1; }
	var lo := 0;
	var hi := |s| - 1;
	var mid := 0;
	ind := -1;
	while(lo <= hi && ind < 0)
		decreases hi-lo + (if 0 <= ind then 0 else 1);						
		invariant 0 <= lo <= |s| && -1 <= hi < |s| && 0 <= mid < |s|		
		invariant !member(s[..lo],n)										
		invariant !member(s[hi+1..],n)										
		invariant -1 <= ind < |s|											
		invariant 0 <= ind ==> s[ind] == n									
	{
		mid := lo + (hi - lo)/2;
		if (s[mid] == n) { 
			ind := mid;
		} else if (s[mid] < n) {
			lo := mid + 1;
		} else {
			hi := mid - 1;
		}
	}
}

