predicate ordenadoEntre(a:array<int>, e:int, d:int)
requires 0 <= e <= d <= a.Length
reads a
{
    forall i,j ::e <= i <= j < d ==> a[i] <= a[j]
}
predicate ordenado(a:array<int>)
reads a
{
    ordenadoEntre(a,0,a.Length)
}
method bubbleSort(a:array<int>)
ensures ordenado(a)
ensures permutacao(a[..], old(a[..]))
modifies a
{
    if a.Length > 1
    {
        var i := 1;
        while i < a.Length
        invariant 1 <= i <= a.Length
        invariant ordenadoEntre(a,0,i)
        invariant permutacao(a[..], old(a[..]))
        {
            bubbleStep(a,i);
            i := i + 1;
        }
    }
}
method bubbleStep(a: array<int>, i: int)
requires 0 <= i < a.Length
requires ordenadoEntre(a,0,i)
ensures ordenadoEntre(a,0,i+1)
ensures permutacao(a[..], old(a[..]))
modifies a
{
    var j := i;
    while j>0 && a[j-1] > a[j]
    invariant 0 <= j <= i
    invariant ordenadoEntre(a,0,j) && ordenadoEntre(a,j,i+1)
    invariant 1 < j + 1 <= i ==> a[j-1] <= a[j+1]
    invariant permutacao(a[..], old(a[..]))
    {
        a[j-1], a[j] := a[j], a[j-1];
        j := j - 1;
    }
}

function R(n: nat): nat 
    decreases n
{
    if n == 0 then 0 else if R(n-1) > n then R(n-1) - n else R(n-1) + n
}
method calcR(n: nat) returns (r: nat)
    ensures r == R(n) 
{
    r := 0;
    var i := 0;
    while i < n 
        decreases n - i
        invariant 0 <= i <= n && r == R(i)
    {
      assert i < n && 0 <= i <= n && r == R(i); 
      assert (r > i + 1 && 0 <= i + 1 <= n && r - i - 1 == R(i + 1)) || (r <= i + 1 && 0 <= i + 1 <= n && r + i + 1 == R(i + 1)); 
        i := i + 1;
      assert (r > i && 0 <= i <= n && r - i == R(i)) || (r <= i && 0 <= i <= n && r + i == R(i)); 
        if r > i {
            r := r - i;
        } 
        else {
            r := r + i;
        }
      assert 0 <= i <= n && r == R(i); 
    }
}

