method Search( s: seq<int>, x: int ) returns ( k: int )
    requires forall p,q | 0 <= p < q < |s| :: s[p] <= s[q];
    ensures 0 <= k <= |s|;
    ensures forall i | 0 <= i < k :: s[i] <= x;
    ensures forall i | k <= i < |s| :: s[i] >= x;
    ensures forall z | z in s[..k] :: z <= x;
    ensures forall z | z in s[k..] :: z >= x;
    ensures s == s[..k]+s[k..];
{
    var p := 0;
    var q := |s|;
    if p == q
    {
        return p;
    }
    while p != q 
        decreases q-p;
        invariant 0 <= p <= q <= |s|;
        invariant forall r | 0 <= r < p :: s[r] <= x;
        invariant forall r | q <= r < |s| :: s[r] >= x;
    {
        var m := p + (q-p)/2;
        if s[m] == x
        {
            return m;
        }
        if s[m] < x
        {
            p := m+1;
        }
        else
        {
            q := m;
        }
    }
    return p;
}
method Sort( m: multiset<int> ) returns ( r: seq<int> )
    ensures multiset(r) == m;
    ensures forall p,q | 0 <= p < q < |r| :: r[p] <= r[q];
{
    r := [];
    var rest := m;
    while rest != multiset{}
        decreases rest;
        invariant m == multiset(r)+rest;
        invariant forall p,q | 0 <= p < q < |r| :: r[p] <= r[q];
    {
        var x :| x in rest;
        rest := rest - multiset{x};
        var k := Search(r, x);
        r := r[..k] + [x] + r[k..];
    }
    return r;
}

method Reverse(a:array<int>, n:int) returns (b:array<int>)
    requires 0 <= n <= a.Length
    ensures b.Length == a.Length
    ensures forall j :: (0<= j < n) ==> b[j] == a[n-1-j]
    ensures forall j :: (n<= j < b.Length) ==> b[j] == a[j]
{
    b := new int[a.Length];
    var i := 0;
    while i < n
        decreases n-i
        invariant 0 <= i <= n
        invariant forall j :: (0<= j < i) ==> b[j] == a[n-1-j]
    {
        b[i] := a[n-1-i];
        i := i + 1;
    }
    assert forall j :: (0<= j < n) ==> b[j] == a[n-1-j];
    while i < a.Length
        decreases a.Length - i
        invariant n <= i <= a.Length
        invariant forall j :: (0<= j < n) ==> b[j] == a[n-1-j];
        invariant forall j :: (n <= j < i) ==> b[j] == a[j];
    {
        b[i] := a[i];
        i := i + 1;
    }
    assert i == a.Length;
}
