lemma exp_even (x:real, n:nat)
  requires n % 2 == 0;
  ensures (exp (x, n) == exp (x*x, n/2));
{
  assume (false);
}
lemma exp_odd(x:real, n:nat)
  requires n % 2 == 1;
  ensures (exp (x, n) == exp (x*x, (n-1)/2)*x);
{
  assume (false);
}
function exp (x: real, n:nat): real
{
  if (x == 0.0) then 0.0
  else if (n == 0) then 1.0
  else x * exp(x, n-1)
}
method exp_by_sqr (x0: real, n0: nat) returns (r: real)
  requires x0 >= 0.0;
  ensures r == exp (x0, n0);
{
  if (x0 == 0.0) { return 0.0; }
  if (n0 == 0) { return 1.0; }
  var x, n, y  := x0, n0, 1.0;
  while (n > 1)
    invariant 1 <= n <= n0;
    invariant exp (x0, n0) == exp (x, n) * y;
  {
    if (n % 2 == 0)
    {
      exp_even(x, n);
      x := x * x;
      n := n / 2;
    }
    else
    {
      exp_odd(x,n);
      y := x * y;
      x := x * x;
      n := (n-1)/2;
    }
  }
  return x * y;
}

method LinearSearch(a: array<int>, e: int) returns (n:int)
  requires exists i::0<=i<a.Length && a[i]==e
  ensures 0<=n<a.Length && a[n]==e
  ensures forall k :: 0 <= k < n ==> a[k]!=e
{
  n :=0;
  while n!=a.Length
    invariant 0<=n<=a.Length
    invariant forall i::0<=i<n ==> e!=a[i]
  {
    if e==a[n]{
      return;
    }
    n:=n+1;
  }
}

predicate isSorted(a: array<int>)
  reads a
{
    forall i, j :: 0 <= i < j < a.Length ==> a[i] <= a[j]
}
method binarySearch(a: array<int>, x: int) 
    returns (index: int)
    requires isSorted(a)
    ensures -1 <= index < a.Length
    ensures index == -1 ==> x !in a[..]
    ensures index >= 0  ==> a[index] == x
{   
    var low, high := 0, a.Length;
    while low < high 
        decreases high - low
        invariant 0 <= low <= high <= a.Length
        invariant x !in a[..low]
        invariant x !in a[high..]
    {
        var mid := low + (high - low) / 2;
        if 
        {
            case a[mid]  < x => low := mid + 1;
            case a[mid]  > x => high := mid;
            case a[mid] == x => return mid;
        }
    }
    return -1;
}
method Main() {
    var a := new int[5];
    a[0], a[1], a[2], a[3], a[4] := 1, 5, 10, 12, 15;
    var x0 := binarySearch(a, 4);
    var x1 := binarySearch(a, 5);
    assert x0 == -1;
}

