function count(items: seq<int>, item: int): nat
  decreases |items|
{
  if |items| == 0 then 0 else
    (if items[|items|-1] == item then 1 else 0)
        + count( items[..|items|-1], item )
}
method occurences(items: array<int>, item: int) returns (num: nat)
  requires items != null
  ensures num <= items.Length
  ensures num == count( items[..], item )
{
  var i: nat := 0;
  num := 0;
  while i < items.Length
    invariant num <= i <= items.Length
    invariant num == count( items[..i], item )
  {
    assert items[..i+1] == items[..i] + [items[i]];
    if items[i] == item
      { num := num + 1; }
    i := i + 1;
  }
  assert items[..i] == items[..];
}

function More(x: int): int {
  if x <= 0 then 1 else More(x - 2) + 3
}
lemma {:induction false} Increasing(x: int)
  ensures x < More(x)
{
  if x <= 0 {
    calc {
      x;
    <=  { assert x <= 0; }
      0;
    <  
      1;
    ==  
      More(x);
    }
  } else {
    calc {
      true;
    ==>  { Increasing(x - 2); }
      x - 2 < More(x - 2);
    ==  
      x + 1 < More(x - 2) + 3;
    ==  
      x + 1 < More(x);
    ==>  
      x < More(x);
    }
  }
}

method Product(m: nat, n: nat) returns (res: nat)
  ensures res == m*n;
{
  var m1: nat := m;
  res := 0;
  while (m1!=0)
    invariant m1 >= 0;
    invariant res == (m - m1) * n;
    decreases m1;
  {
    var n1: nat := n;
    while (n1 != 0)
      invariant n1 >= 0;
      invariant res == (m - m1) * n + (n-n1);
      decreases n1;
    {
      res := res+1;
      n1 := n1-1;
    }
    m1 := m1-1;
  }
}
method ProductGhost(m: nat, n: nat) returns (res: nat)
  ensures res == m*n;
{
  var m1: nat := m;
  res := 0;
  while (m1!=0)
    invariant m1 >= 0;
    invariant res == (m - m1) * n;
    decreases m1;
  {
    var n1: nat := n;
    ghost var pre_res := res;
    while (n1 != 0)
      invariant n1 >= 0;
      invariant res == pre_res + (n-n1);
      decreases n1;
    {
      res := res+1;
      n1 := n1-1;
    }
    m1 := m1-1;
  }
}
method ProductThree(m: nat, n: nat, p: nat) returns (res: nat)
  ensures res == m*n*p;
{
  var m1: nat := 0;
  res := 0;
  while (m1!=m)
    invariant m1 <= m;
    invariant res == m1*n*p;
    decreases m - m1;
  {
    var n1: nat := 0;
    ghost var res_l2 := res;
    while (n1!=n)
      invariant n1 <= n;
      invariant res == res_l2 + n1*p;
      decreases n - n1;
    {
      var p1: nat := 0;
      ghost var res_l3 := res;
      while (p1!=p)
        invariant p1 <= p;
        invariant res == res_l3 + p1;
        decreases p - p1;
      {
        res := res+1;
        p1 := p1+1;
      }
      n1 := n1+1;
    }
    m1:= m1+1;
  }
}

