method removeDonor(donors: seq <array<int>>, v: int) returns (updated : seq<array<int>>)
requires donors != []
requires |donors| > 0
requires forall r : int :: (0 <= r < |donors|  ==> donors[r] != null);
requires forall k : int :: (0 <= k < |donors|  ==> donors[k].Length == 7) 
requires exists k : int :: 0 <= k < |donors|  && donors[k][0] == v 
ensures forall k : int :: ( 0 <= k < |donors|   ==> ((donors[k][0] != v) ==> (donors[k] in updated)))
ensures forall k : int :: (0 <= k < |updated|  ==> (updated[k] != null));
{
    var i : int := 0;
    updated := [];
    while (i < |donors| )
    decreases |donors|  - i
    invariant 0 <= i <= |donors| 
    invariant forall j : int :: (0 <= j < i ==> (donors[j][0] != v) ==> (donors[j] in updated))
    invariant forall j : int :: (0 <= j < |updated| ==> (updated[j] != null))
    {
       if(donors[i][0] != v){
           updated := updated + [donors[i]];
       }
        i := i + 1;
    }
}

method ArraySplit (a : array<int>) returns (b : array<int>, c : array<int>)
  ensures fresh(b)
  ensures fresh(c)
  ensures a[..] == b[..] + c[..]
  ensures a.Length == b.Length + c.Length
  ensures a.Length > 1 ==> a.Length > b.Length
  ensures a.Length > 1 ==> a.Length > c.Length
{
  var splitPoint : int := a.Length / 2;
  b := new int[splitPoint];
  c := new int[a.Length - splitPoint];
  var i : int := 0;
  while (i < splitPoint)
    invariant 0 <= i <= splitPoint
    invariant b[..i] == a[..i]
  {
    b[i] := a[i];
    i := i + 1;
  }
  var j : int := 0;
  while (i < a.Length)
    invariant splitPoint <= i <= a.Length
    invariant j == i - splitPoint
    invariant c[..j] == a[splitPoint..i]
    invariant b[..] + c[..j] == a[..i]
  {
    c[j] := a[i];
    i := i + 1;
    j := j + 1;
  }
}

function sorted(a:array<int>, n:int):bool
    requires 0 <= n <= a.Length
    reads a
{ forall i, j:: (0 <= i < j < n) ==> a[i] <= a[j] }
method binarySearch(a:array<int>, n:int, e:int) returns (z:int)
  requires 0 < n <= a.Length
  requires sorted(a,n)
  ensures n > z >= 0 ==> a[z] == e
  ensures z < 0 ==> forall k :: 0 <= k < n ==> a[k] != e 
{
    var start := 0;
    var end := n;
    var mid := 0;
    while(start < end)
    decreases end-start;
    invariant 0 <= start <= end <= n && 0 <= mid;
    invariant forall j ::  0<= j < n && !(start <= j < end) ==> a[j] != e
    {
      mid := (start + end) / 2;     
      if e > a[mid]{start := mid +1;}
      else if e < a[mid]{end := mid;}
      else{return mid;}
    }
    return -mid-1;
}

function fib(n: nat): nat
decreases n
{
    if n<=1 then n else fib(n-1)+fib(n-2)
}
method fibfast(n: nat) returns (c: nat)
requires n>=1;
ensures c==fib(n);
{
    var p := 0;
    c := 1;
    var i := 1;
    while(i<n)
    invariant 1<=i<=n
    invariant p == fib(i-1) && c == fib(i)
    decreases (n-i)
    {
        var new2 := p + c;
        p := c;
        c := new2;
        i := i + 1;
    }
}

predicate isIndex(i: int, s: string) { 0 <= i < |s| }
predicate isPrefixF(pre: string, str: string) { |pre| <= |str| && pre == str[..|pre|] }
method isPrefix(pre: string, str: string) returns (res: bool)
    ensures res <==> isPrefixF(pre, str)
{
    if |str| < |pre|
    || pre != str[..|pre|]
    {
        res := false;
    }
    else
    {
        res := true;
    }
    return res;
}
predicate isSubstringF(sub: string, str: string)
{
    |sub| == 0 ||
    (|sub| <= |str| &&
     (exists k | 0 <= k <= |str| - |sub|
               :: isPrefixF(sub, str[k..])))
}
method isSubstring(sub: string, str: string) returns (res: bool)
    ensures res <==> isSubstringF(sub, str)
{
    if |sub| == 0 { return true; }
    if |sub| > |str| { return false; }
    res := false;
    var i := 0;
    while i <= |str| - |sub|
        decreases |str| - |sub| - i
        invariant 0 <= i <= |str|
        invariant !res <==> (forall a | 0 <= a < i :: !isPrefixF(sub, str[a..]))
    {
        res := isPrefix(sub, str[i..]);
        if res { return true; } 
        i := i + 1;
    }
    return false;
}
predicate haveCommonKSubstringF(k: nat, str1: string, str2: string) {
    k == 0 ||
    (k <= |str1| &&
     k <= |str2| &&
     (exists a | 0 <= a <= |str1| - k :: isSubstringF(str1[a..][..k], str2)))
}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
    ensures found <==> haveCommonKSubstringF(k, str1, str2)
{
    if k == 0 { return true; }
    if k > |str1| || k > |str2| { return false; }
    found := false;
    var i := 0;
    while i + k <= |str1|
        decreases |str1| - k - i
        invariant 0 <= i <= |str1| - k + 1
        invariant !found ==> forall a | 0 <= a < i :: !isSubstringF(str1[a..][..k], str2)
    {
        found := isSubstring(str1[i..][..k], str2);
        if found { return true; }
        i := i + 1;
    }
    return false;
}
