  ensures r < 0 ==> forall i :: 0 <= i < a.Length ==> a[i] != key
  {
    var lo, hi := 0, a.Length;
    while lo < hi 
    invariant 0 <= lo <= hi <= a.Length
    invariant forall i :: 0 <= i < lo ==> a[i] != key
    invariant forall i :: hi <= i < a.Length ==> a[i] != key
    decreases hi - lo
    {
      var mid := lo+(hi-lo)/2;
      if a[mid] > key {
        hi := mid;
      }
      else if a[mid] < key {
        lo := mid + 1; 
      }
      else {
        return mid;
      }
    }
    return -1;
  }
