datatype Expp = Const(valeur:int) | Var(string) | Plus(Expp, Expp) |  Mult(Expp, Expp)
function eval(e:Expp, store:map<string, int>):int
decreases e, store
{
	match(e)
		case Const(n) => n
		case Var(s) => if(s in store) then store[s] else -1
		case Plus(e1, e2) => eval(e1, store) + eval(e2, store)
		case Mult(e1, e2) => eval(e1, store) * eval(e2, store)
}
function optimize(e:Expp):Expp
decreases e
{
	match (e) 
	  case Mult(e1, e2) => if e1==Const(0) then Const(0) 
	                       else if e1==Const(1) then e2 
						   else if e2==Const(0) then Const(0)
						   else if e2==Const(1) then e1
						   else if e1.Const? && e2.Const? then Const(e1.valeur*e2.valeur)
						   else Mult(e1, e2)
	  case Plus(e1, e2) => if e1==Const(0) then e2
	                       else if e2==Const(0) then e1
						   else if e1.Const? && e2.Const? then Const(e1.valeur+e2.valeur)
						   else Plus(e1, e2)
	  case Const(n)     => Const(n)
	  case Var(s)       => Var(s)
}
method optimizeCorrect(e:Expp, s:map<string, int>)
 decreases e, s
ensures eval(e,s) == eval(optimize(e), s)
{
	match (e) {
		case Mult(e1, e2) => {
			if e1==Const(0) {
			assert eval(optimize(e), s)
			    == eval(optimize(Mult(e1, e2)), s)
				== eval(optimize(Mult(Const(0), e2)), s)
				== eval(Const(0), s)
				== 0
				== 0 * eval(e2, s)
				== eval(Const(0), s) * eval(e2, s)
				== eval(e1, s) * eval(e2, s)
				== eval(Mult(e1, e2), s)
				== eval(e, s);
			} else if e1==Const(1) {
				assert eval(optimize(e), s)
				    == eval(optimize(Mult(e1, e2)), s)
					== eval(optimize(Mult(Const(1), e2)), s)
					== eval(e2, s)
					== eval(e2, s) * 1
					== eval(e2, s) * eval(Const(1), s)
					== eval(e2, s) * eval(e1, s)
					== eval(e1, s) * eval(e2, s)
					== eval(e, s);
			} else if e2==Const(0) {
				assert eval(optimize(e), s)
				    == eval(optimize(Mult(e1, e2)), s)
					== eval(optimize(Mult(e1, Const(0))), s)
					== eval(Const(0), s)
					== 0
					== 0 * eval(e1, s)
					== eval(Const(0), s) * eval(e1, s)
					== eval(e1, s) * eval(e2, s)
					== eval(e, s);
			} else if e2==Const(1) {
				assert eval(optimize(e), s)
				    == eval(optimize(Mult(e1, e2)), s)
					== eval(optimize(Mult(e1, Const(1))), s)
					== eval(e1, s)
					== eval(e1, s) * 1
					== eval(e1, s) * eval(Const(1), s)
					== eval(e1, s) * eval(e2, s)
					== eval(e, s);
			} else if e1.Const? && e2.Const? {
				assert eval(optimize(e), s)
				    == eval(Const(e1.valeur*e2.valeur), s)
					== e1.valeur * e2.valeur
					== eval(e1, s) * eval(e2, s)
					== eval(Mult(e1, e2), s)
					== eval(e, s);
			} else {
				assert eval(optimize(e), s)
				    == eval(optimize(Mult(e1, e2)), s)
					== eval(Mult(e1, e2), s)
					== eval(e, s);
			}
		}
		case Plus(e1, e2) => {
			if e1==Const(0) {
				assert eval(optimize(e), s)
				    == eval(optimize(Plus(e1, e2)), s)
					== eval(optimize(Plus(Const(0), e2)), s)
					== eval(e2, s)
					== eval(e2, s) + 0
					== eval(e2, s) + eval(Const(0), s)
					== eval(e2, s) + eval(e1, s)
					== eval(e1, s) + eval(e2, s)
					== eval(e, s);
			} else if e2==Const(0) {
				assert eval(optimize(e), s)
				    == eval(optimize(Plus(e1, e2)), s)
					== eval(optimize(Plus(e1, Const(0))), s)
					== eval(e1, s)
					== eval(e1, s) + 0
					== eval(e1, s) + eval(Const(0), s)
					== eval(e1, s) + eval(e2, s)
					== eval(e, s);
			} else if e1.Const? && e2.Const? {
				assert eval(optimize(e), s)
				    == eval(Const(e1.valeur+e2.valeur), s)
					== e1.valeur + e2.valeur
					== eval(e1, s) + eval(e2, s)
					== eval(Plus(e1, e2), s)
					== eval(e, s);
			} else {
				assert eval(optimize(e), s)
				    == eval(optimize(Plus(e1, e2)), s)
					== eval(Plus(e1, e2), s)
					== eval(e, s);
			}
		}
		case Const(n) => {
			assert eval(optimize(e), s)
			    == eval(optimize(Const(n)), s)
				== eval(Const(n), s)
				== eval(e, s);
		}
		case Var(a) => {
			assert eval(optimize(e), s)
			    == eval(optimize(Var(a)), s)
				== eval(Var(a), s)
				== eval(e, s);
		}
	}
}
method optimizeFeatures()
{
	assert( optimize(Mult(Var("x"), Const(0))) == Const(0) );
	assert( optimize(Mult(Var("x"), Const(1))) == Var("x") );
	assert( optimize(Mult(Const(0), Var("x"))) == Const(0) );
	assert( optimize(Mult(Const(1), Var("x"))) == Var("x") );
	assert( optimize(Plus(Const(0), Var("x"))) == Var("x") );
	assert( optimize(Plus(Var("x"), Const(0))) == Var("x") );
	assert( optimize(Plus(Const(3), Const(4))) == Const(7) );
	assert( optimize(Mult(Const(3),Const(4))) == Const(12) );
	assert( optimize(Plus(Plus(Var("x"), Var("y")), Const(0))) == Plus(Var("x"), Var("y")) );
}

method find(a: array<int>, key: int) returns(index: int)
    requires a.Length > 0;
    ensures 0 <= index <= a.Length;
    ensures index < a.Length ==> a[index] == key;
{
    index := 0;
    while index < a.Length && a[index] != key 
        decreases a.Length - index 
        invariant 0 <= index <= a.Length
        invariant forall x :: 0 <= x < index ==> a[x] != key
    {
        index := index + 1;
    }
}

predicate sorted(a:array<int>, low:int, high:int) 
    requires 0 <= low <= high <= a.Length;
    reads a;
{
    forall j, k :: low <= j < k < high ==> a[j] <= a[k]
}
method selection_sort(a:array<int>)
    modifies a;
    requires a.Length > 0
    ensures multiset(a[..]) == old(multiset(a[..]));
    ensures sorted(a, 0, a.Length)
{
    var max : int;
    var i: int;
    i := a.Length-1;
    max := i;
    while i > 0
    invariant 0 <= i < a.Length;
    invariant multiset(a[..]) == old(multiset(a[..]))
    invariant sorted(a, i, a.Length); 
    invariant forall j :: 0 <= j < i < a.Length-1 ==> a[j] <= a[i+1] 
    decreases i;
    {
        max := select(a, i);
        a[i], a[max] := a[max], a[i];
        i := i-1;
    }
}
method select(a:array<int>, i: int) returns (m:int)
    requires 0 <= i < a.Length
    ensures 0 <= m <= i
    ensures multiset(a[..]) == old(multiset(a[..]))
    ensures forall x :: 0 <= x <= i ==> a[x] <= a[m]
{
    var j := i;
    m := j;
    while j > 0
    invariant 0 <= j <= i
    invariant 0 <= m <= i
    invariant forall x:: j <= x <= i ==> a[m] >= a[x]
    invariant multiset(a[..]) == old(multiset(a[..]))
    decreases j
    {
        j := j-1;
        if( a[j] > a[m])
        {
            m := j;
        }
    } 
}
