datatype Door = Closed | Open
method InitializeDoors(n:int) returns (doors:array<Door>)
  requires n >= 0
  ensures doors != null && fresh(doors) && doors.Length == n
  ensures forall j :: 0 <= j < doors.Length ==> doors[j] == Closed;
{
  doors := new Door[n];
  var i := 0;
  while i < doors.Length
    invariant i <= doors.Length
    invariant forall j :: 0 <= j < i ==> doors[j] == Closed;
  {
    doors[i] := Closed;
    i := i + 1;
  }
}
method Main ()
{
  var doors := InitializeDoors(100);
  var pass := 1;
  while pass <= doors.Length
  {
    var door := pass;
    while door < doors.Length
    {
      doors[door] := if doors[door] == Closed then Open else Closed;
      door := door + pass;
    }
    pass := pass + 1;
  }
  var i := 0;
  while i < doors.Length
  {
    print i, " is ", if doors[i] == Closed then "closed\n" else "open\n";
    i := i + 1;
  }
}

datatype Nat = Zero | Succ(pred: Nat)
{
  function MemberToInt(): int {
    match this
    case Zero => 0
    case Succ(p) => 1 + p.MemberToInt()
  }
}
function ExternalToInt(n: Nat): int {
  match n
  case Zero => 0
  case Succ(p) => 1 + ExternalToInt(p)
}
function Prefix(n: Nat, len: nat): Nat
  requires len <= n.MemberToInt() && len <= ExternalToInt(n)
  ensures ExternalToInt(Prefix(n, len)) == len  
  ensures Prefix(n, len).MemberToInt() == len  
{
  if len == 0 then Zero else Succ(Prefix(n.pred, len - 1))
}

datatype Cell = Cell(x: int)
type EvenCell = c: Cell | c.x % 2 == 0 witness Cell(0)
function doubleEvenCell(f: EvenCell): int
{
  if f.x % 2 == 1 then 1/0 else f.x * 2
}
method Main() {
  var x: set<Cell> := { Cell(1), Cell(2), Cell(3), Cell(4) };
  var b := forall g :: g in x ==> doubleEvenCell(g) > 0;
  assert b;
}

function Fat(n:nat):nat
{
    if n == 0 then 1 else n*Fat(n-1)
}
method Fatorial(n:nat) returns (f:nat)
ensures f == Fat(n)
{
    f := 1;
    var i := 1;
    while i <= n
        decreases n-i 
        invariant 1 <= i <= n+1 
        invariant f == Fat(i-1) 
    {
        f := f * i;
        i := i + 1;
    }
    return f;
}

method Deduplicate(a:array<int>, n:int) returns (b:array<int>, m:int)
  requires a.Length >= 0
  requires 0 <= n <= a.Length
  requires sorted(a,n)
  ensures b.Length >= 0
  ensures 0 <= m <= b.Length
  ensures b.Length <= a.Length
  ensures 0 <= m <= n
  ensures sorted(b,m) && unique(b,0,m)
  ensures forall k :: 0 <= k < m ==> contains (a, n, b[k])
  ensures forall k :: 0 <= k < n ==> contains (b, m, a[k])
{
  m := 0;
  b := new int[n];
  var i:= 0;
  while i<n
    decreases n-i
    invariant 0 <= m <= i <= n
    invariant 0 <= m <= b.Length
    invariant forall k :: 0 <= k < m ==> contains (a, n, b[k])
    invariant forall k :: 0 <= k < i ==> contains (b, m, a[k])
    invariant forall j :: 0 <= j < m && i < n ==> b[j] < a[i]
    invariant forall j,k :: (0 <= j < k < m) ==> b[j] < b[k]
  {
   var value:= a[i];
   b[m] := value;
   assert forall k :: 0 <= k < i ==> contains (b, m, a[k]);
   i:= i+1;
   m:= m+1;
   var initGroup := i-1;
   while i<n && a[i] == value
     decreases n-i
     invariant i <= n <= a.Length
     invariant 0 <= initGroup <= i
     invariant forall j :: initGroup <= j < i ==> a[j] == value
   {
       i:= i+1;  
    }
  }
}
function sorted(a:array<int>, n:int):bool
    requires 0 <= n <= a.Length
    reads a
{ 
    forall i, j:: (0 <= i < j < n) ==> a[i] <= a[j] 
}
function unique(b:array<int>, l:int, h:int):bool
    reads b;
    requires 0 <= l <= h <= b.Length
{ 
     forall k::(l<=k<h) ==> forall j::(k<j<h) ==> b[k] != b[j] 
}
function contains(b:array<int>, m:int, v:int):bool
    requires 0 <= m <= b.Length 
    reads b
{
    v in b[..m]
}

method FindMax(a: array<int>) returns (idx: int)
  requires a.Length >= 1;
  ensures 0 <= idx < a.Length;
  ensures forall k :: 0 <= k < a.Length ==> a[k] <= a[idx]; 
{
  var i := 1;
  idx := 0;
  var max := a[idx];
  while (i < a.Length)
    invariant 0 <= i <= a.Length;
    invariant 0 <= idx < i;
    invariant max == a[idx];
    invariant forall k :: 0 <= k < i ==> max >= a[k];
  {
    if (a[i] >= max) {
      idx := i;
      max := a[idx];
    }
    i := i + 1;
  }
  return idx;
}
method FindMaxVal(a: array<int>) returns (val: int)
  requires a.Length >= 1;
  ensures forall k :: 0 <= k < a.Length ==> a[k] <= val;
  ensures exists j :: 0 <= j < a.Length && a[j] == val;
{
  var i := 0;
  val := a[0];
  while (i < a.Length)
    invariant i <= a.Length;
    invariant forall j :: 0 <= j < i ==> a[j] <= val;
    invariant exists j :: 0 <= j < a.Length && a[j] == val;
  {
    if (a[i] >= val)
    {
      val := a[i];
    }
    i := i + 1;
  }
  return val;
}

module Sets {
  lemma ProperSubsetImpliesSmallerCardinality<T>(a: set<T>, b: set<T>)
    requires a < b
    ensures |a| < |b|
  {
    assert |b| == |a| + |b-a|;
  }
  lemma SetInclusionImpliesSmallerCardinality<T>(a: set<T>, b: set<T>)
    requires a <= b
    ensures |a| <= |b|
  {
    assert b == a + (b - a);
  }
  lemma SetInclusionImpliesStrictlySmallerCardinality<T>(a: set<T>, b: set<T>)
    requires a < b
    ensures |a| < |b|
  {
    assert b == a + (b - a);
  }
  lemma SetInclusionAndEqualCardinalityImpliesSetEquality<T>(a: set<T>, b: set<T>)
    requires a <= b
    requires |a| == |b|
    ensures a == b
  {
    assert b == a + (b - a);
  }
  function SetRange(n: int) : set<int>
  {
    set i | 0 <= i < n
  }
  lemma CardinalitySetRange(n: int)
  requires n >= 0
  ensures |SetRange(n)| == n
  {
    if n == 0 {
    } else {
      CardinalitySetRange(n-1);
      assert SetRange(n)
          == SetRange(n-1) + {n-1};
    }
  }
  lemma SubSetExtensionality<T>(a: set<T>, b: set<T>)
    requires forall x | x in a :: x in b
    ensures a <= b
  {
  }
}

predicate sorted_between (a:array<int>, from:nat, to:nat)
  reads a;
  requires a != null;
  requires from <= to;
  requires to <= a.Length;
{
  forall i,j :: from <= i < j < to && 0 <= i < j < a.Length ==> a[i] <= a[j]
}
predicate sorted (a:array<int>)
  reads a;
  requires a!=null;
{
  sorted_between (a, 0, a.Length)
}
method bubbleSort (a: array<int>)
  modifies a;
  requires a != null;
  requires a.Length > 0;
  ensures sorted(a);
  ensures multiset(old(a[..])) == multiset(a[..]);
{
  var i:nat := 1;
  while (i < a.Length)
    invariant i <= a.Length;
    invariant sorted_between (a, 0, i);
    invariant multiset(old (a[..])) == multiset(a[..]);
  {
    var j:nat := i;
    while (j > 0)
      invariant 0 <= j <= i;
      invariant sorted_between (a, 0, j);
      invariant forall u,v:: 0 <= u < j < v < i+1 ==> a[u] <= a[v];
      invariant sorted_between (a, j, i+1);
      invariant multiset(old (a[..])) == multiset(a[..]);
    {
      if (a[j-1] > a[j]) {
        var temp:int := a[j-1];
        a[j-1] := a[j];
        a[j] := temp;
      }
      j := j - 1;
    }
    i := i+1;
  }
}

