method FindMax(a: array<int>) returns (i: int)
   requires 0 < a.Length
   ensures 0 <= i < a.Length && forall k :: 0 <= k < a.Length ==> a[i] >= a[k]
{
   var count := 0;
   i := 0;
   while count < a.Length
        invariant 0 <= count <= a.Length
        invariant 0 <= i < a.Length 
        invariant forall k :: 0 <= k < count ==> a[i] >= a[k]
        decreases a.Length - count
   {
        if a[count] >= a[i] {i := count;}
        count := count + 1;
   }
}
method FindMaxi(a: array<int>) returns (max: int)
	requires 0 < a.Length
	ensures forall k :: 0 < k < a.Length ==> max >= a[k]
{
	var index := 0;
	max := a[0]; 
	while index < a.Length
		invariant 0 <= index <= a.Length
		invariant forall k :: 0 <= k < index ==> max >= a[k]
        decreases a.Length - index
	{
		if a[index] >= max { max := a[index]; }
		index := index + 1;
	}
}

module Crawler {
  datatype Variables = Variables(x:int, y:int)
  predicate Init(s:Variables) {
    && s.x == 0
    && s.y == 5
  }
  predicate MoveNorth(s:Variables, s':Variables) {
    && s'.x == s.x
    && s'.y == s.y + 1
  }
  predicate MoveSouthEast(s:Variables, s':Variables) {
    && s'.x == s.x + 1
    && s'.y == s.y - 1
  }
  predicate Next(s:Variables, s':Variables) {
    || MoveNorth(s, s')
    || MoveSouthEast(s, s')
  }
  predicate InManhole(s:Variables) {
    s.x*s.x + s.y*s.y <= 3*3
  }
  predicate Safety(s:Variables) {
    !InManhole(s)
  }
  predicate Inv(s:Variables) {
    s.x + s.y >= 5
  }
  lemma SafetyTheorem(s:Variables, s':Variables)
    ensures Init(s) ==> Inv(s)
    ensures Inv(s) && Next(s, s') ==> Inv(s')
    ensures Inv(s) ==> Safety(s)
  {
  }
}

method Find(a: array<int>, key: int) returns (index: int)
  requires a != null;
  ensures -1 <= index < a.Length;
  ensures 0 <= index < a.Length ==> a[index] == key;
  ensures index == -1 ==> forall x :: 0 <= x < a.Length ==> a[x] != key;
  ensures old(a) == a;
  ensures old(a[..]) == a[..];
{
  var i := 0;
  while (i < a.Length)
    invariant i <= a.Length;
    invariant forall k :: 0 <= k < i ==> a[k] != key
  {
    if (a[i] == key) { return i; }
    i := i + 1;
  }
  return -1;
}

