datatype BT<T> = Tip(T) | Node(BT<T>, BT<T>)
method Main()
{
	var tree: BT<int>;
	tree := Tip(1);
	var f: seq<int> := FrontierIter(tree);
	assert f == [1];
	tree := Node(tree, Tip(2));
	f := FrontierIter(tree);
	assert f == [1,2];
}
function Frontier<T>(tree: BT<T>): seq<T>
{
	match tree {
		case Tip(n) => [n]
		case Node(left, right) => Frontier(left) + Frontier(right)
	}
}
function Fseq<T>(forest:seq<BT<T>>) : seq<T>
{
	if forest == [] then [] else Frontier(forest[0]) + Fseq(forest[1..])
}
function ForestSize<T>(stack : seq<BT<T>>): nat
{
	if stack == [] then 0
	else TreeSize(stack[0]) + ForestSize(stack[1..])
}
function TreeSize<T>(tree : BT<T>) : nat
{
	match tree {
		case Tip(n) => 1
		case Node(left, right) => TreeSize(left) + TreeSize(right) + 1
	}
}
method FrontierIter<T>(tree: BT<T>) returns (f: seq<T>)
	ensures f == Frontier(tree)
{
	var stack: seq<BT<T>>;
	stack := [tree];
	f := [];
	while stack != []
		invariant f + Fseq(stack) == Frontier(tree)
		decreases ForestSize(stack)
	{
	ghost var V0 := ForestSize(stack);
		match stack[0]
			case Tip(n) =>
				f:= f + [n];
				stack := stack[1..];
			case Node(left, right) =>
				stack := [left, right] + stack[1..];
		assert 0 <= ForestSize(stack) < V0;
	}
	assert stack == [] && f + Fseq(stack) == Frontier(tree);
	assert f == Frontier(tree);
}

method MultipleReturns (x:int, y:int) returns (more:int, less:int)
requires y > 0
ensures less < x < more
{
assert x - y < x < x + y;
more := x + y;
assert x - y < x < more;
less := x - y;
assert less < x < more;
}
function factorial (n:int): int
requires n >= 0
{
if n == 0 then 1 else n * factorial(n-1)
}
method ComputeFact (n: int) returns (f:int)
requires n >= 0
ensures f == factorial(n)
{
var x := n;
f := 1;
while 
      x > 0
  invariant f * factorial(x) == factorial(n)
{
assert f * (x * factorial (x-1)) == factorial (n);
f, x := f * x, x-1;
assert f * factorial(x) == factorial(n);
}
}
method compute_Fact2 (n:int) returns (f:int)
requires n >= 0
ensures f == factorial(n)
{
var x := 0;
f := 1;
assert f == factorial(x);
while 
         x < n 
		 invariant 0 <= x <= n 
		 invariant f == factorial(x)
{
x, f := x + 1, f * (x+1);
}
}
method Square (a:int) returns (x:int)
requires a >= 1
ensures x == a*a
{
x := 1;
var y := 1;
while y < a
     invariant 1 <= y <= a
	 invariant x == y*y
	{
	assert x + 2*y + 1 == (y+1)*(y+1);
	y, x := y + 1, x + 2*y + 1; 
	assert x == y*y;
	}
}
function sumSerie(n:int):int
requires n >= 1
{
if n == 1 then 1 else sumSerie(n-1) + 2*n - 1
}
method Square2 (a:int) returns (x:int)
requires a >= 1
ensures x == a*a
{
x := 1;
var y := 1;
while y < a
     invariant 1 <= y <= a
	 invariant x == sumSerie(y)
	{
	y := y + 1;
	x := x + 2*y - 1;
	}
assert 1 <= y <= a && x == sumSerie(y) && y >= a;
assert x == sumSerie(a) ; 
sumSerieProp_Lemma(a);
assert x == a*a; 
}
lemma sumSerieProp_Lemma (n:int)
requires n >= 1
ensures sumSerie(n) == n*n
{}
function power(b:int, e:nat):int
{
if e == 0 then 1 else b * power(b,e-1)
}
method compute_power (b:int, e:nat) returns (p:int)
ensures p == power(b,e)
{
var t , x := e, b;
p := 1;
while t > 0
     invariant  0 <= t <= e
	 invariant p * power(x,t) == power(b,e)  
    {
	if t % 2 == 0 {
	              assume p * power(x*x,t/2) == power(b,e);
	              x, t := x*x, t/2;
				  assume p * power(x,t) == power(b,e);
	              }
	else {
	      p, t := p*x, t-1;
	      }
	}
}
lemma even_Lemma (b:int, e:nat)
requires e%2 == 0
ensures power(b,e) == power(b*b,e/2)

