method ComputeFib(n: nat) returns (b: nat)
  ensures b == fib(n)  
{
  var i: int := 0;
  var c: int := 1;
  b := 0;
  while i < n
    invariant 0 <= i <= n
    invariant c == fib(i + 1)
    invariant b == fib(i)
  {
    b, c := c, b + c;
    i := i + 1;
  }
}
method m_ex11()
