function sorted(a:array<int>, n:int):bool
    requires 0 <= n <= a.Length
    reads a
{ 
    forall i, j :: (0 <= i < j < n) ==> a[i] <= a[j]
}
function unique(a:array<int>, n:int):bool
    requires 0 <= n <= a.Length
    reads a
{ 
    forall i :: (0 <= i < n) ==> forall j :: (i < j < n) ==> a[i] != a[j]
}
function contains(b:array<int>, m:int, v:int):bool
    requires 0 <= m <= b.Length 
    reads b
{
    v in b[..m]
}
method Deduplicate(a:array<int>, n:int) returns (b:array<int>, m:int)
    requires 0 <= n <= a.Length
    requires sorted(a,n) 
    ensures 0 <= m <= b.Length
    ensures sorted(b,m) && unique(b,m)
    ensures forall k :: (0 <= k < m) ==> contains(a,n,b[k])
    ensures fresh(b)
{
    b := new int[n];
    if (n == 0) {
        return b,0;
    }
    b[0] := a[0];
    var i := 1;
    m:=1;
    assert contains(b,1,a[0]);
    while (i < n)
        decreases n-i
        invariant 1 <= i <= n
        invariant 1 <= m <= i 
        invariant sorted(b, m)
        invariant unique(b, m)
        invariant forall k :: (0 <= k < m) ==> contains(a,n,b[k])
        {
            if (a[i] > b[m-1]) {
                b[m] := a[i];
                m := m + 1;
                assert contains (b,m,a[i]); 
            }
            i := i + 1;
        }
}

datatype option<A> = None | Some(get: A)
datatype ty =  TBase                             
            |  TArrow(T1: ty, T2: ty)            
datatype tm = tvar(id: int)                      
            | tapp(f: tm, arg: tm)               
            | tabs(x: int, T: ty, body: tm)      
predicate value(t: tm)
{
  t.tabs?
}
function fv(t: tm): set<int> 
{
  match t
  case tvar(id) => {id}
  case tabs(x, T, body) => fv(body)-{x}
  case tapp(f, arg) => fv(f)+fv(arg)
}
function subst(x: int, s: tm, t: tm): tm 
{
  match t
  case tvar(x') => if x==x' then s else t
  case tabs(x', T, t1) => tabs(x', T, if x==x' then t1 else subst(x, s, t1))
  case tapp(t1, t2) => tapp(subst(x, s, t1), subst(x, s, t2))
}
function step(t: tm): option<tm>
{
       if (t.tapp? && t.f.tabs? && value(t.arg)) then
  Some(subst(t.f.x, t.arg, t.f.body))
         else if (t.tapp? && step(t.f).Some?) then
  Some(tapp(step(t.f).get, t.arg))
         else if (t.tapp? && value(t.f) && step(t.arg).Some?) then
  Some(tapp(t.f, step(t.arg).get))
  else None
}
predicate reduces_to(t: tm, t': tm, n: nat)
  decreases n;
{
  t == t' || (n > 0 && step(t).Some? && reduces_to(step(t).get, t', n-1))
}
lemma lemma_step_example1(n: nat)
  requires n > 0;
  ensures reduces_to(tapp(tabs(0, TArrow(TBase, TBase), tvar(0)), tabs(0, TBase, tvar(0))),
                     tabs(0, TBase, tvar(0)), n);
{
}
function find(c: map<int,ty>, x: int): option<ty>
{
  if (x in c) then Some(c[x]) else None
}
function extend(x: int, T: ty, c: map<int,ty>): map<int,ty>
{
  c[x:=T]
}
function has_type(c: map<int,ty>, t: tm): option<ty>
  decreases t;
{
  match t
    case tvar(id) => find(c, id)
    case tabs(x, T, body) =>
  var ty_body := has_type(extend(x, T, c), body);
                     if (ty_body.Some?) then
  Some(TArrow(T, ty_body.get))          else None
    case tapp(f, arg) =>
  var ty_f   := has_type(c, f);
  var ty_arg := has_type(c, arg);
                     if (ty_f.Some? && ty_arg.Some?) then
  if ty_f.get.TArrow? && ty_f.get.T1 == ty_arg.get then
  Some(ty_f.get.T2)  else None else None
}
lemma example_typing_1()
  ensures has_type(map[], tabs(0, TBase, tvar(0))) == Some(TArrow(TBase, TBase));
{
}
lemma example_typing_2()
  ensures has_type(map[], tabs(0, TBase, tabs(1, TArrow(TBase, TBase), tapp(tvar(1), tapp(tvar(1), tvar(0)))))) ==
          Some(TArrow(TBase, TArrow(TArrow(TBase, TBase), TBase)));
{
  var c := extend(1, TArrow(TBase, TBase), extend(0, TBase, map[]));
  assert find(c, 0) == Some(TBase);
  assert has_type(c, tvar(0)) == Some(TBase);
  assert has_type(c, tvar(1)) == Some(TArrow(TBase, TBase));
  assert has_type(c, tapp(tvar(1), tapp(tvar(1), tvar(0)))) == Some(TBase);
}
lemma nonexample_typing_1()
  ensures has_type(map[], tabs(0, TBase, tabs(1, TBase, tapp(tvar(0), tvar(1))))) == None;
{
  var c := extend(1, TBase, extend(0, TBase, map[]));
  assert find(c, 0) == Some(TBase);
  assert has_type(c, tapp(tvar(0), tvar(1))) == None;
}
lemma nonexample_typing_3(S: ty, T: ty)
  ensures has_type(map[], tabs(0, S, tapp(tvar(0), tvar(0)))) != Some(T);
{
  var c := extend(0, S, map[]);
  assert has_type(c, tapp(tvar(0), tvar(0))) == None;
}
lemma theorem_progress(t: tm)
  requires has_type(map[], t).Some?;
  ensures value(t) || step(t).Some?;
{
}
lemma {:induction c, t} lemma_free_in_context(c: map<int,ty>, x: int, t: tm)
  requires x in fv(t);
  requires has_type(c, t).Some?;
  ensures find(c, x).Some?;
  decreases t;
{
}
predicate closed(t: tm)
{
  forall x :: x !in fv(t)
}
lemma corollary_typable_empty__closed(t: tm)
  requires has_type(map[], t).Some?;
  ensures closed(t);
{
  forall (x:int) ensures x !in fv(t);
  {
    if (x in fv(t)) {
      lemma_free_in_context(map[], x, t);
      assert false;
    }
  }
}
lemma {:induction t} lemma_context_invariance(c: map<int,ty>, c': map<int,ty>, t: tm)
  requires has_type(c, t).Some?;
  requires forall x: int :: x in fv(t) ==> find(c, x) == find(c', x);
  ensures has_type(c, t) == has_type(c', t);
  decreases t;
{
  if (t.tabs?) {
    assert fv(t.body) == fv(t) || fv(t.body) == fv(t) + {t.x};
    lemma_context_invariance(extend(t.x, t.T, c), extend(t.x, t.T, c'), t.body);
  }
}
lemma lemma_substitution_preserves_typing(c: map<int,ty>, x: int, s: tm, t: tm)
  requires has_type(map[], s).Some?;
  requires has_type(extend(x, has_type(map[], s).get, c), t).Some?;
  ensures has_type(c, subst(x, s, t)) == has_type(extend(x, has_type(map[], s).get, c), t);
  decreases t;
{
  var S := has_type(map[], s).get;
  var cs := extend(x, S, c);
  var T  := has_type(cs, t).get;
  if (t.tvar?) {
    if (t.id==x) {
      assert T == S;
      corollary_typable_empty__closed(s);
      lemma_context_invariance(map[], c, s);
    }
  }
  if (t.tabs?) {
    if (t.x==x) {
      lemma_context_invariance(cs, c, t);
    } else {
      var cx  := extend(t.x, t.T, c);
      var csx := extend(x, S, cx);
      var cxs := extend(t.x, t.T, cs);
      lemma_context_invariance(cxs, csx, t.body);
      lemma_substitution_preserves_typing(cx, x, s, t.body);
    }
  }
}
lemma theorem_preservation(t: tm)
  requires has_type(map[], t).Some?;
  requires step(t).Some?;
  ensures has_type(map[], step(t).get) == has_type(map[], t);
{
  if (t.tapp? && value(t.f) && value(t.arg)) {
    lemma_substitution_preserves_typing(map[], t.f.x, t.arg, t.f.body);
  }
}
predicate normal_form(t: tm)
{
  step(t).None?
}
predicate stuck(t: tm)
{
  normal_form(t) && !value(t)
}
lemma corollary_soundness(t: tm, t': tm, T: ty, n: nat)
  requires has_type(map[], t) == Some(T);
  requires reduces_to(t, t', n);
  ensures !stuck(t');
  decreases n;
{
  theorem_progress(t);
  if (t != t') {
   theorem_preservation(t);
   corollary_soundness(step(t).get, t', T, n-1);
  }
}

predicate isPrefixPred(pre:string, str:string)
{
	(|pre| <= |str|) && 
	pre == str[..|pre|]
}
predicate isNotPrefixPred(pre:string, str:string)
{
	(|pre| > |str|) || 
	pre != str[..|pre|]
}
lemma PrefixNegationLemma(pre:string, str:string)
	ensures  isPrefixPred(pre,str) <==> !isNotPrefixPred(pre,str)
	ensures !isPrefixPred(pre,str) <==>  isNotPrefixPred(pre,str)
{}
method isPrefix(pre: string, str: string) returns (res:bool)
	ensures !res <==> isNotPrefixPred(pre,str)
	ensures  res <==> isPrefixPred(pre,str)
{
	return |pre| <= |str| && forall i :: 0 <= i < |pre| ==> pre[i] == str[i];
}
predicate isSubstringPred(sub:string, str:string)
{
	(exists i :: 0 <= i <= |str| &&  isPrefixPred(sub, str[i..]))
}
predicate isNotSubstringPred(sub:string, str:string)
{
	(forall i :: 0 <= i <= |str| ==> isNotPrefixPred(sub,str[i..]))
}
lemma SubstringNegationLemma(sub:string, str:string)
	ensures  isSubstringPred(sub,str) <==> !isNotSubstringPred(sub,str)
	ensures !isSubstringPred(sub,str) <==>  isNotSubstringPred(sub,str)
{}
method isSubstring(sub: string, str: string) returns (res:bool)
	ensures  res <==> isSubstringPred(sub, str)
	ensures  res ==> isSubstringPred(sub, str)
	ensures  isSubstringPred(sub, str) ==> res
	ensures  isSubstringPred(sub, str) ==> res
	ensures !res <==> isNotSubstringPred(sub, str) 
{
	if(|str| < |sub|)
	{
		return false;
	}
	else
	{
		var i: nat := 0;
	 	res := false;
		while (i <= |str|-|sub| && res == false)
		decreases |str| - |sub| - i + (if !res then 1 else 0)
		invariant 0 <= i <= |str|-|sub| + 1
		invariant res ==> isSubstringPred(sub,str)
		invariant forall j :: 0 <= j < i ==> isNotPrefixPred(sub, str[j..])
		{
			res := isPrefix(sub,str[i..]);
			if(!res)
			{
				i := i + 1;
			}
		}
	}
}
predicate haveCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	exists i1, j1 :: 0 <= i1 <= |str1|- k && j1 == i1 + k && isSubstringPred(str1[i1..j1],str2)
}
predicate haveNotCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	forall i1, j1 :: 0 <= i1 <= |str1|- k && j1 == i1 + k ==>  isNotSubstringPred(str1[i1..j1],str2)
}
lemma commonKSubstringLemma(k:nat, str1:string, str2:string)
	ensures  haveCommonKSubstringPred(k,str1,str2) <==> !haveNotCommonKSubstringPred(k,str1,str2)
	ensures !haveCommonKSubstringPred(k,str1,str2) <==>  haveNotCommonKSubstringPred(k,str1,str2)
{}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
	ensures found  <==>  haveCommonKSubstringPred(k,str1,str2)
	ensures !found <==> haveNotCommonKSubstringPred(k,str1,str2) 
{
	if (k <= |str1| && k <= |str2|)
	{
		var slice : string;
		found := false;
		var i: nat := 0;
		while (i <= |str1| - k && found == false)
		decreases |str1| - k - i + (if !found then 1 else 0)
		invariant found ==> haveCommonKSubstringPred(k,str1,str2)
		invariant forall x, y :: 0 <= x < i && found == false && y == x + k && y <= |str1| ==> isNotSubstringPred(str1[x..y], str2)		
		{
			slice := str1[i..i+k];
			found := isSubstring(slice, str2);
				i := i + 1;
		}
	} else {
		return false;
	}
}
method maxCommonSubstringLength(str1: string, str2: string) returns (len:nat)
	requires (|str1| <= |str2|)
	ensures (forall k :: len < k <= |str1| ==> !haveCommonKSubstringPred(k,str1,str2))
	ensures haveCommonKSubstringPred(len,str1,str2)
{
	assert isPrefixPred(str1[0..0],str2[0..]);
	len := |str1|;
	var hasCommon : bool := true;
	while(len > 0)
		decreases len
		invariant forall i :: len < i <= |str1| ==> !haveCommonKSubstringPred(i,str1,str2)
	{
		hasCommon := haveCommonKSubstring(len, str1, str2);
		if(hasCommon){
			return len;
		}
		len := len - 1;
	}
	return len;
}

method Carre(a: nat) returns (c: nat)
ensures c == a*a
{
    var i := 0;
    c := 0;
    while i != a
    invariant 0 <= i <= a
    invariant c == i*i
    decreases a - i
  {
    c := c + 2*i +1;
    i := i + 1;
  }
}

method divu10(n: bv16) returns (r: bv16)
  ensures r == n/10;
 {
  var q: bv16;
  q := (n >> 1) + (n >> 2);
  q := q + (q >> 4);
  q := q + (q >> 8);
  q := q >> 3;
  r := n - (((q << 2) + q) << 1);
  return q + if (r > 9) then 1 else 0;
}

method TestSeq( x: seq<int> )
{
    assert x == x[0..|x|];
    assert forall k | 0 <= k <= |x| :: x[0..k]+x[k..|x|] == x;
    assert forall k | 0 <= k <= |x| :: x[0..k] == x[..k];
    assert forall k | 0 <= k <= |x| :: x[k..] == x[k..|x|];
    assert forall k | 0 <= k <= |x| :: x[..k]+x[k..] == x;
    assert forall z | z in x :: exists i | 0 <= i < |x| :: z==x[i];
    assert forall z | z in multiset(x) :: exists i | 0 <= i < |x| :: z==x[i];
    assert forall z :: z in multiset(x) <==> exists i | 0 <= i < |x| :: z==x[i];
    assert forall p,q | 0 <= p <= q <= |x| :: x[..p]+x[p..q]+x[q..] == x;
    if x != []
    {
        assert x[0] in x;
        assert x[|x|-1] in x;
        assert multiset(x) == multiset{x[0]}+multiset(x[1..]);
        var i :| i in x;
        if 0 <= i < |x|
        {
            assert multiset(x) == multiset(x[..i])+multiset(x[i..]);
        }
    }
    else
    {
        assert |x| == 0;
        assert multiset(x) == multiset{};
        var s: set<int> := {};
        assert |s| == 0;
        assert multiset(x) == multiset(s);
    }
}
method TestMultiset( x: multiset<int> )
{
    assert x == multiset{} <==> |x| == 0;
    if |x| == 0
    {
        assert x == multiset{};
    }
    else
    {
        var z:| z in x;
        assert z in x;
        var x' := x-multiset{z};
        assert |x| == |x'|+1;
        assert x+multiset{z} != x;
        assert |x+multiset{z}| == |x|+1;
        assert x == x'+multiset{z};
    }
}
method TestSet( x: set<int> )
{
    assert x == {} <==> |x| == 0;
    if |x| == 0
    {
        assert x == {};
    }
    else
    {
        var z:| z in x;
        assert z in x;
        var x' := x-{z};
        assert |x| == |x'|+1;
        assert x+{z} == x == x'+{z};
    }
}

class DishWasher{
    var loaded: bool;
    var addedDtgt: bool;
    var washed: bool;
    predicate Valid()
      reads this;
    {
      (!this.loaded && this.washed)||(!this.addedDtgt && this.washed) ==> false
    }
    constructor()
      modifies this;
      ensures this.loaded == false;
      ensures this.addedDtgt == false;
      ensures this.washed == false;
      ensures Valid();
    {
        this.loaded := false;
        this.addedDtgt := false;
        this.washed := false;
    }
    method Load()
      modifies this;
      ensures this.loaded == true;
      ensures this.washed == false;
      ensures Valid();
      ensures this.addedDtgt == old(this.addedDtgt);
    {
        this.loaded := true;
        this.washed := false;
    }
    method AddDtgt()
      modifies this;
      ensures this.addedDtgt == true;
      ensures this.loaded == old(this.loaded);
      ensures this.washed == old(this.washed);
    {
        this.addedDtgt := true;
    }
    method Wash()
      modifies this;
      requires this.addedDtgt == true;
      requires this.loaded == true;
      ensures this.washed == true;
      ensures this.loaded == old(this.loaded);
    {
        this.addedDtgt := false;
        this.washed := true;
    }
    method Unload()
      modifies this;
      requires this.loaded == true;
      requires this.washed == true;
      ensures this.loaded == false;
      ensures this.washed == false;
      ensures Valid();
      ensures this.addedDtgt == old(this.addedDtgt);
    {
        this.loaded := false;
        this.washed := false;
    }
}
