predicate Sorted(a:array<int>)
  reads a;
  requires a != null; 
{
  forall m, n :: 0 <= m < n < a.Length ==> a[m] <= a[n]
}
predicate SortedBetween(a:array<int>,lo:int,hi:int)
  reads a;
  requires a != null;
  requires 0 <= lo <= hi < a.Length;
{
  forall m, n :: lo <= m < n <= hi ==> a[m] <= a[n]
}
method MergeSort(u:array<int>) returns (a:array<int>)
  requires u != null && u.Length > 0;
  ensures a != null;
  ensures a.Length == u.Length;
  ensures Sorted(a);
{
  a := RecursiveMerge(u,0,u.Length-1);
  return;
}
method RecursiveMerge(u:array<int>,lo:int,hi:int) returns (a:array<int>)
  requires u != null && u.Length > 0;
  requires 0 <= lo <= hi < u.Length;
  decreases hi-lo;
  ensures a != null;
  ensures a.Length == u.Length;
  ensures SortedBetween(a,lo,hi);
  ensures forall i:: 0 <= i < lo ==> a[i] == u[i]; 
  ensures forall i:: hi < i < a.Length ==> a[i] == u[i]; 
{
  a := new int[u.Length];
  forall (k | 0 <= k < u.Length)  {a[k] := u[k];}
  if (lo >= hi)
  {
    return;
  }
  else
  {
    var mid:int := (lo+hi) / 2;
    a := RecursiveMerge(a,lo,mid);
    a := RecursiveMerge(a,mid+1,hi);
    a := Merge(a,lo,mid,hi);
    return;
  }
}
method Merge(u:array<int>, lo:int, mid:int, hi:int) returns (a:array<int>)
requires u != null && u.Length > 0;
requires 0 <= lo <= mid <= hi < u.Length;
requires SortedBetween(u,lo,mid); 
requires hi <= mid || SortedBetween(u,mid+1,hi);
ensures a != null;
ensures a.Length == u.Length;
ensures SortedBetween(a,lo,hi);
ensures forall i:: 0 <= i < lo ==> a[i] == u[i] 
ensures forall i:: hi < i < u.Length ==> a[i] == u[i] 
decreases hi-lo; 
{
  a := new int[u.Length];
  forall (k | 0 <= k < u.Length)  {a[k] := u[k];}
  var tmp := new int[hi-lo+1];
  var x:int := lo;
  var y:int := mid + 1;
  var i:int := 0;
  while (i < hi-lo+1)
    invariant forall k:: 0 <= k < u.Length ==> a[k] == u[k];
    invariant 0 <= i <= hi-lo+1; 
    invariant lo <= x <= mid+1; 
    invariant mid+1 <= y <= hi+1; 
    invariant (x-lo) + (y-(mid+1)) == i; 
    invariant i == 0 || SortedBetween(tmp,0,i-1); 
    invariant forall q, r:: 0 <= q < i && (x <= r <= mid || y <= r <= hi) ==> tmp[q] <= a[r]; 
  {
    if (x > mid) 
    {
      tmp[i] := a[y];
      y := y + 1;
    }
    else if (y > hi) 
    {
      tmp[i] := a[x];
      x := x + 1;
    }
    else if (a[x] <= a[y]) 
    {
      tmp[i] := a[x];
      x := x + 1;
    }
    else
    {
      tmp[i] := a[y]; 
      y := y + 1;
    }
    i := i + 1;
  }
  i := 0;
  while (i < hi-lo+1)
    invariant 0 <= i <= hi-lo+1;
    invariant forall q  :: (0 <= q < lo || hi < q < u.Length) ==> a[q] == u[q]; 
    invariant forall q:: forall r:: 0 <= q < r < hi-lo+1 ==>  tmp[q] <= tmp[r]; 
    invariant forall q:: lo <= q < lo+i ==> tmp[q-lo] == a[q]; 
    invariant forall q:: forall r:: lo <= q < r < lo+i ==> a[q] <= a[r]; 
  {
    a[lo + i] := tmp[i];
    i := i + 1;
  }
}
