predicate Sorted(q: seq<int>)
{
	forall i,j :: 0 <= i <= j < |q| ==> q[i] <= q[j] 
}
predicate Left(q:seq<int>, left:nat, key:int){
	0<=left<|q| && q[left] == key
}
method IterateRight(q:seq<int>, key:int,prev:nat)returns(next:nat)
	requires Sorted(q)
	requires |q| > 0
	requires 0<=prev<|q|
	requires q[prev] == key
	ensures q[prev] == key
	ensures next < |q| ==> q[next]>key
	ensures next <= |q| ==> q[next-1] == key
	ensures prev<=next<=|q|
	decreases |q| - prev
{
	if prev+1 == |q| {next:=|q|;}
	else if q[prev+1] == key {next:=IterateRight(q,key,prev+1);}
	else {next:=prev+1;}
}
method FindInitialRight(q:seq<int>, key:int, seed:nat)returns(right:nat)
	requires Sorted(q)
	requires |q| > 0
	requires 0<=seed<|q|
	requires q[seed] == key
	ensures seed < right <= |q| ==> q[right-1]==key
	ensures right < |q| ==> q[right] > key
	ensures right<=|q|
{
	if |q| == 1 || seed == |q| - 1{right:=|q|;}
	else{right:=IterateRight(q,key,seed);}
}
method FindInitialLeft(q:seq<int>, key:int, seed:nat)returns(left:nat)
	requires Sorted(q)
	requires |q| > 0
	requires 0<=seed<|q|
	requires q[seed] == key
	ensures Left(q,left,key)
	ensures left>0 ==> q[left-1]< key;
{
	if seed == 0 {left:=0;}
	else{	left:=IterateLeft(q,key,seed);}
}
method IterateLeft(q:seq<int>, key:int,prev:nat)returns(next:nat)
	requires Sorted(q)
	requires |q| > 0
	requires 0<prev<|q|
	requires q[prev] == key
	ensures next <= prev
	ensures q[next] == key
	ensures next>0 ==> q[next-1] < key
	decreases prev
{
	if prev-1 == 0 {
		if q[prev-1] == key{next:=0;}
		else {next:=1;}
	}
	else if q[prev-1] == key {next:=IterateLeft(q,key,prev-1);}
	else {next:=prev;}
}
method FindRange(q: seq<int>, key: int) returns (left: nat, right: nat)
	requires Sorted(q)
	ensures left <= right <= |q|
	ensures forall i :: 0 <= i < left ==> q[i] < key
	ensures forall i :: right <= i < |q| ==> q[i] > key
	ensures forall i :: left <= i < right ==> q[i] == key
  {
		if |q| == 0 || key < q[0] {left:=0;right:=0;}
		else if q[|q| - 1] < key {left:=|q|;right:=|q|;}
		else if |q| == 1 {
			if q[0] == key {left:=0;right:=|q|;}
			else {left:=0;right:=0;}
		}
		else{
			left,right:= FindRangeInNotTrivialSeq(q,key);
		}
	}
method FindRangeInNotTrivialSeq(q:seq<int>, key:int) returns(left:nat, right:nat)
	requires Sorted(q)
	requires |q| > 1
	requires q[|q|-1]>=key
	requires q[0]<=key
	ensures left <= right <= |q|
	ensures forall i :: 0 <= i < left ==> q[i] < key
	ensures forall i :: right <= i < |q| ==> q[i] > key
	ensures forall i :: left <= i < right ==> q[i] == key
{
		if q[0] == key {left:=0;right:=FindInitialRight(q,key, 0);}
		else if q[|q|-1] == key {right:=|q|;left:=FindInitialLeft(q,key, |q|-1);}
		else{
			var mid: int := BinarySearch(q, key);
			if mid < |q| && q[mid] == key{
				right := FindInitialRight(q,key, mid);
				left := FindInitialLeft(q,key, mid);
			}
			else {
				assert key !in q;
				right:= mid;
				left:=mid;
			}
		}
}
method BinarySearch(q: seq<int>, key: int) returns (mid: nat)
  requires Sorted(q)
	requires |q| > 1
	requires q[0] < key
	requires q[|q|-1] > key
	ensures key in q ==> 0<=mid<|q|
	ensures key in q ==> q[mid] == key
	ensures key !in q ==> 0<=mid<=|q|
	ensures key !in q && 0<=mid<|q| ==> q[mid] > key
	ensures key !in q && 0<=mid-1<|q| ==> q[mid - 1] < key
{
  var lo, hi := 0, |q|;
  while lo < hi
    invariant 0 <= lo <= hi <= |q|
    invariant key !in q[..lo] && key !in q[hi..]
		decreases hi - lo
  {
    mid := (lo + hi) / 2;
    if key < q[mid] {
      hi := mid;
    } else if q[mid] < key{
      lo := mid + 1;
    } else {
			assert key in q;
			return mid;
    }
  }
	assert key !in q;
	assert lo >= hi;
	assert hi >= 0;
	if lo >= |q| {lo:=|q| - 1;}
	else if lo == 0 {lo:=1;}
	var seed:nat;
	assert 0<lo<|q|;
	if q[lo] > key {seed:=lo;}
	else if lo+1<|q| && q[lo+1] > key {seed:=lo+1;}
	else if lo + 2 < |q| && q[lo+2] > key {seed:=lo+2;}
	else {seed:=|q|-1;}
	mid := FindNearestBiggest(q,key,seed);
}
method FindNearestBiggest(q:seq<int>,key:int,seed:nat)returns(mid:nat)
requires key !in q
requires 0<seed<|q|
requires q[0] < key
requires q[|q| - 1] > key
requires q[seed] > key
ensures 0<mid<|q|
ensures q[mid] > key
ensures q[mid-1] < key
{
	mid:=seed;
	assert q[mid] > key;
	if seed - 1 == 0 {mid:=seed;}
	else if q[seed - 1] > key {mid:=FindNearestBiggest(q,key,seed - 1);}
	else { mid:=seed;}
}

ghost function f(x: int, y: int): int
lemma Associativity(x: int, y: int, z: int)
  ensures f(x, f(y, z)) == f(f(x, y), z)
lemma Monotonicity(y: int, z: int)
  requires y <= z
  ensures forall x :: f(x, y) <= f(x, z)
lemma DiagonalIdentity(x: int)
  ensures f(x, x) == x
method CalculationalStyleProof(a: int, b: int, c: int, x: int)
  requires c <= x == f(a, b)
  ensures f(a, f(b, c)) <= x
{
  calc {
    f(a, f(b, c));
  ==  { Associativity(a, b, c); }
    f(f(a, b), c);
  ==  { assert f(a, b) == x; }
    f(x, c);
  <=  { assert c <= x; Monotonicity(c, x); }
    f(x, x);
  ==  { DiagonalIdentity(x); }
    x;
  }
}
method DifferentStyleProof(a: int, b: int, c: int, x: int)
  requires A: c <= x
  requires B: x == f(a, b)
  ensures f(a, f(b, c)) <= x
{
  assert 0: f(a, f(b, c)) == f(f(a, b), c) by {
    Associativity(a, b, c);
  }
  assert 1: f(f(a, b), c) == f(x, c) by {
    reveal B;
  }
  assert 2: f(x, c) <= f(x, x) by {
    assert c <= x by { reveal A; }
    Monotonicity(c, x);
  }
  assert 3: f(x, x) == x by {
    DiagonalIdentity(x);
  }
  assert 4: f(a, f(b, c)) == f(x, c) by {
    reveal 0, 1;
  }
  assert 5: f(x, c) <= x by {
    reveal 2, 3;
  }
  assert f(a, f(b, c)) <= x by {
    reveal 4, 5;
  }
}

function fib(n: nat): nat
decreases n
{
   if n == 0 then 0 else
   if n == 1 then 1 else
                  fib(n - 1) + fib(n - 2)
}
method fibonacci1(n:nat) returns (f:nat)
ensures f==fib(n)
{
   var i := 0;
   f := 0;
   var fsig := 1;
   while i < n
      decreases n - i
      invariant f==fib(i) && fsig==fib(i+1)
      invariant i<=n
   {
      f, fsig := fsig, f + fsig;
      i := i + 1;
   }
}
method fibonacci2(n:nat) returns (f:nat)
ensures f==fib(n)
{
if (n==0) {f:=0;}
else{
   var i := 1;
   var fant := 0;
   f := 1;
   while i < n
      decreases n-i
      invariant fant==fib(i-1) && f==fib(i)
      invariant i<=n
   {
      fant, f := f, fant + f;
      i := i + 1;
   }
}
}
method fibonacci3(n:nat) returns (f:nat)
ensures f==fib(n)
{
{
   var i: int := 0;
   var a := 1;
       f := 0; 
   while i < n
    decreases n-i
    invariant 0<=i<=n
    invariant if i ==0 then a==fib(i+1) && f==fib(i)
               else a==fib(i-1) && f==fib(i)
   {
      a, f := f, a + f; 
      i := i + 1;
   }
}
}

method ANCD(aqpr: seq<char>) returns (retx: bool)
{
  if |aqpr| == 0 {
    return true;
  }
  var abcd := 0;
  var efgh := |aqpr| - 1;
  retx := true;
  while (abcd < efgh)
    invariant 0 <= abcd <= efgh + 1 && 0 <= efgh < |aqpr|
    invariant abcd + efgh == |aqpr| - 1
    invariant (forall ijkl :: 0 <= ijkl < abcd ==> aqpr[ijkl] == aqpr[|aqpr| - ijkl - 1])
  {
    if aqpr[abcd] != aqpr[efgh] {
      retx := false;
      return;
    }
    abcd := abcd + 1;
    efgh := efgh - 1;
  }
}

method GCD1(a: int, b: int) returns (r: int)
    decreases a % b
    requires a > 0 && b > 0 && a != b
{
    if a < b {
        r := GCD1(b, a);
    } else if (a % b == 0) {
        r := b;
    } else {
        r := GCD1(b, a % b);
    }
}
method GCD2(a: int, b: int) returns (r: int)
    decreases b + 1
    requires a >= 0 && b >= 0
{
    if b == 0 {
        r := a;
    } else {
        r := GCD2(b, a % b);
    }
}

class LimitedStack{
      var capacity : int; 
      var arr : array?<int>; 
      var top : int; 
      predicate Valid()
      reads this;
      {
        !(arr == null) && (capacity > 0) && (arr.Length == capacity) && (-1<=top<=capacity-1)
      }
      predicate Empty()
      reads this;
      {
        (top == -1)
      }
      predicate Full()
      reads this;
      {
        (top == capacity-1)
      }
      method Init(c : int)
      modifies this;
      requires c>0;
      ensures fresh(arr); 
      ensures this.capacity == c;
      ensures Valid() && Empty();
      {
        capacity := c;
        arr := new int[c];
        top := -1;
      }
      method isEmpty() returns (res : bool)
      requires Valid();
      ensures res == Empty();
      {
        if top == -1
        {res := true;}
        else
        {res := false;}
      }
      method Peek() returns (elem : int)
      requires Valid();
      requires !Empty();
      ensures elem == arr[top];
      ensures arr == old(arr);
      ensures forall i:int :: 0<=i<=top ==> arr[i]==old(arr[i]);
      {
        elem := arr[top];
      }
      method Push(elem : int)
      modifies arr;
      modifies `top;
      requires Valid() && !Full();
      ensures Valid();
      ensures top == old(top) + 1;
      ensures elem == arr[top];
      ensures forall i:int :: 0<=i<=old(top) ==> arr[i]==old(arr[i]);
      {
        top := top + 1;
        arr[top] := elem;
      }
      method Pop() returns (elem : int)
      modifies `top;
      requires Valid() && !Empty();
      ensures top == old(top)-1;
      ensures elem == arr[top+1];
      ensures arr == old(arr);
      {
        elem := arr[top];
        top := top-1;
      }
      method Shift()
      requires Valid() && !Empty();
      ensures Valid();
      ensures forall i : int :: 0 <= i < capacity - 1 ==> arr[i] == old(arr[i + 1]);
      ensures top == old(top) - 1;
      modifies this.arr, this`top;
      {
        var i : int := 0;
        while (i < capacity - 1 )
        invariant 0 <= i < capacity;
        invariant top == old(top);
        invariant forall j : int :: 0 <= j < i ==> arr[j] == old(arr[j + 1]);
        invariant forall j : int :: i <= j < capacity ==> arr[j] == old(arr[j]);
        {
          arr[i] := arr[i + 1];
          i := i + 1;
        }
        top := top - 1;
      }
      method Push2(elem : int)
      modifies arr;
      modifies `top;
      requires Valid();
      ensures Valid() && !Empty();
      ensures elem == arr[top];
      ensures (old(top) < capacity-1) ==> (top == old(top)+1) && forall i:int :: 0<=i<top ==> arr[i]==old(arr[i]);
      ensures (old(top) == capacity-1) ==> (top == old(top)) && forall i:int :: 0<=i<top-1 ==> arr[i]==old(arr[i+1]);
      {
        if(top == capacity - 1)
        {Shift();Push(elem);}
        else
        {Push(elem);}
      } 
      method Main(){
           var s := new LimitedStack;
           s.Init(3);
           assert s.Empty() && !s.Full(); 
           s.Push(27);
           assert !s.Empty();
           var e := s.Pop();
           assert e == 27;
           s.Push(5);
           s.Push(32);
           s.Push(9);
           assert s.Full();
           var e2 := s.Pop();
           assert e2 == 9 && !s.Full(); 
           assert s.arr[0] == 5;
           s.Push(e2);
           s.Push2(99);
           var e3 := s.Peek();
           assert e3 == 99;
           assert s.arr[0] == 32;          
       }
}

type T
ghost predicate Below(x: T, y: T)
lemma Reflexive(x: T)
  ensures Below(x, x)
lemma Transitive(x: T, y: T, z: T)
  ensures Below(x, y) && Below(y, z) ==> Below(x, z)
ghost function f(x: T): T
ghost function g(x: T): T
lemma Connection(x: T, y: T)
  ensures Below(f(x), y) == Below(x, g(y))
lemma Monotonic(u: T, v: T)
  requires Below(u, v)
  ensures Below(f(u), f(v)) && Below(g(u), g(v))
{
  FMonotonic(u, v);
  GMonotonic(u, v);
}
lemma FMonotonic(u: T, v: T)
  requires Below(u, v)
  ensures Below(f(u), f(v))
{
  calc {
    Below(f(u), f(v));
  ==  { Reflexive(f(v)); }
    Below(f(v), f(v)) ==> Below(f(u), f(v));
  ==  { Connection(v, f(v)); Connection(u, f(v)); }
    Below(v, g(f(v))) ==> Below(u, g(f(v)));
  <== { Transitive(u, v, g(f(v))); }
    Below(u, v);
  }
}
ghost predicate Above(x: T, y: T)
{
  Below(y, x)
}
lemma AboveReflexive(x: T)
  ensures Above(x, x)
{
  Reflexive(x);
}
lemma AboveTransitive(x: T, y: T, z: T)
  ensures Above(x, y) && Above(y, z) ==> Above(x, z)
{
  Transitive(z, y, x);
}
lemma GeneralMonotonic(u: T, v: T, below: (T, T) -> bool, a: T -> T, b: T -> T)
  requires forall x :: below(x, x)  
  requires forall x, y, z :: below(x, y) && below(y, z) ==> below(x, z)  
  requires forall x, y :: below(a(x), y) == below(x, b(y))  
  requires below(u, v)
  ensures below(a(u), a(v))
{
  calc {
    below(a(u), a(v));
  ==  
    below(a(v), a(v)) ==> below(a(u), a(v));
  ==  
    below(v, b(a(v))) ==> below(u, b(a(v)));
  <== 
    below(u, v);
  }
}
lemma FMonotonic'(u: T, v: T)
  requires Below(u, v)
  ensures Below(f(u), f(v))
{
  forall x {
    Reflexive(x);
  }
  forall x, y, z {
    Transitive(x, y, z);
  }
  forall x, y {
    Connection(x, y);
  }
  GeneralMonotonic(u, v, Below, f, g);
}
lemma GMonotonic(u: T, v: T)
  requires Below(u, v)
  ensures Below(g(u), g(v))
{
  forall x {
    AboveReflexive(x);
  }
  forall x, y, z {
    AboveTransitive(x, y, z);
  }
  forall x, y {
    calc {
      Above(g(x), y);
    ==  
      Below(y, g(x));
    ==  { Connection(y, x); }
      Below(f(y), x);
    ==  
      Above(x, f(y));
    }
  }
  GeneralMonotonic(v, u, Above, g, f);
}

