method Reverse(a: array<int>)
    modifies a;
    ensures  forall i | 0 <= i < a.Length :: a[i] == old(a[a.Length - i - 1]);
{
    var l := 0;
    var r := a.Length - 1;
    while l < r
        decreases r - l;
        invariant 0 <= l <= (a.Length + 1) / 2;
        invariant r == a.Length - l - 1;
        invariant forall i | 0 <= i < l :: a[i] == old(a[a.Length - i - 1]);
        invariant forall i | r < i < a.Length :: a[i] == old(a[a.Length - i - 1]);
        invariant forall i | l <= i <= r :: a[i] == old(a[i]);
    {
        a[l], a[r] := a[r], a[l];
        l := l + 1;
        r := r - 1;
    }
}

method CommandCompleter(n: nat, m: nat, b: bool) {
    var R1 := 0;
    var R2 := 0;
    var i := 0;
    var k := 0;
    while i < n
        invariant i==k
        invariant i<=n
    {
        i := i + 1;
        k := k + 1;
    }
    assert k <= n; 
    if (b) {
        R1 := R1 + k;
        assert R1 <= n; 
    } else {
        var j := 0;
        while j < k
            invariant j >= R2
            invariant j <= k
        {
            if (b) {
                R2 := R2 + 1;
                assert j >= R2; 
            }
            j := j+1;
        }
        assert j<=n; 
    }
    assert R1 + R2 <= 2*n; 
}

