datatype Nat = Zero | Succ(n: Nat)
function one(): Nat
{
	Succ(Zero)
}
function add(x: Nat, y: Nat): Nat
{
	match(x) {
		case Zero => y
		case Succ(n) => Succ(add(n, y))
	}
}
function mult(x: Nat, y: Nat): Nat
{
	match(x) {
		case Zero => Zero
		case Succ(n) => add(mult(n, y), y)
	}
}
lemma {:induction false} axm_zero(x: Nat)
	ensures add(x, one()) != Zero
{
}
lemma {:induction false} axm_pluszero(x: Nat)
	ensures add(x, Zero) == x
{
	if (add(x, Zero) != x) {
		match(x) {
			case Zero =>
				calc == {
					add(x, Zero);
					add(Zero, Zero);
					Zero;
					x;
				}
			case Succ(n) =>
				calc == {
					add(x, Zero);
					add(Succ(n), Zero);
					Succ(add(n, Zero));
						{ axm_pluszero(n); } 
					Succ(n);
					x;
				}
		}
	}
}
lemma {:induction false} axm_timeszero(x: Nat)
	ensures mult(x, Zero) == Zero
{
	match (x) {
		case Zero =>
			calc == {
				mult(Zero, Zero);
				Zero;
			}
		case Succ(n) =>
			calc == {
				mult(Succ(n), Zero);
				add(mult(n, Zero), Zero);
					{ axm_pluszero(mult(n, Zero)); }
				mult(n, Zero);
					{ axm_timeszero(n); }
				Zero;
			}
	}
}
lemma {:induction false} axm_successor(x: Nat, y: Nat)
	ensures add(x, one()) == add(y, one()) ==> x == y
{
	calc == {
		add(x, one());
			{ add_lemmatwo(x); }
		Succ(x);
	}
	calc == {
		add(y, one());
			{ add_lemmatwo(y); }
		Succ(y);
	}
}
lemma axm_plussuccessor(x: Nat, y: Nat)
	ensures add(x, add(y, one())) == add(add(x, y), one())
{
  calc == {
		add(x, add(y, one()));
			{ add_associative(x, y, one()); }
		add(add(x, y), one());
	}
}
lemma add_lemmatwo(x: Nat)
	ensures add(x, one()) == Succ(x)
{}
lemma add_associative(x: Nat, y: Nat, z: Nat)
	ensures add(x, add(y, z)) == add(add(x, y), z)
{
	match (x) {
		case Zero =>
			calc == {
				add(Zero, add(y, z));
				add(y, z);
			}
			calc == {
				add(add(Zero, y), z);
				add(y, z);
			}
		case Succ(n) =>
			calc == {
				add(Succ(n), add(y, z));
				Succ(add(n, add(y, z)));
					{ add_associative(n, y, z); }
				Succ(add(add(n, y), z));
			}
			calc == {
				add(add(Succ(n), y), z);
				add(Succ(add(n, y)), z);
				Succ(add(add(n, y), z));
			}
	}
}
lemma add_commutative(x: Nat, y: Nat)
	ensures add(x, y) == add(y, x)
{
	match (x) {
		case Zero =>
			calc == {
				add(Zero, y);
				y;
			}
			calc == {
				add(y, Zero);
					{ axm_pluszero(y); }
				y;
			}
		case Succ(n) =>
			calc == {
				add(Succ(n), y);
				Succ(add(n, y));
					{ add_lemmatwo(add(y, n)); }
				add(add(n, y), one());
			}
			calc == {
				add(y, Succ(n));
					{ add_lemmatwo(n); }
				add(y, add(n, one()));
				  { add_associative(y, n, one()); }
				add(add(y, n), one());
					{ add_commutative(n, y); }
				add(add(n, y), one());
			}
	}
}
lemma axm_timessuccessor(x: Nat, y: Nat)
	ensures mult(x, add(y, one())) == add(mult(x, y), x)
{
	match (x) {
		case Zero =>
			calc == {
				mult(Zero, add(y, one()));
				Zero;
			}
			calc == {
				add(mult(Zero, y), Zero);
					{ axm_pluszero(mult(Zero, y)); }
				mult(Zero, y);
				Zero;
			}
		case Succ(n) =>
			calc == {
				mult(Succ(n), add(y, one()));
				add(mult(n, add(y, one())), add(y, one()));
					{ axm_timessuccessor(n, add(y, one())); }
				add(add(mult(n, y), n), add(y, one()));	
					{ add_associative(mult(n, y), n, add(y, one())); }
				add(mult(n, y), add(n, add(y, one())));
					{ add_associative(n, y, one()); }
				add(mult(n, y), add(add(n, y), one()));
					{ add_commutative(n, y); }
				add(mult(n, y), add(add(y, n), one()));
			}
			calc == {
				add(mult(Succ(n), y), Succ(n));
				add(add(mult(n, y), y), Succ(n));
					{ add_lemmatwo(n); }
				add(add(mult(n, y), y), add(n, one()));
					{ add_associative(mult(n, y), y, add(n, one())); }
				add(mult(n, y), add(y, add(n, one())));
					{ add_associative(y, n, one()); }
				add(mult(n, y), add(add(y, n), one()));
			}
	}	
}

method bubbleSort (a: array<int>)
  requires a != null && a.Length > 1;
  ensures forall u, v :: 0 <= u < v < a.Length ==> a[u] <= a[v];
  modifies a;
{
  var i:nat := 1;
  while (i < a.Length)
    invariant i <= a.Length;
    invariant forall u, v:: 0 <= u < v < i ==> a[u] <= a[v];
  {
    var j:nat := i;
    while (j > 0)
      invariant 0 <= j <= i;
      invariant forall u, v :: 0 <= u < v < j ==> a[u] <= a[v];
      invariant forall u, v :: j <= u < v < i+1 ==> a[u] <= a[v];
      invariant forall u , v :: 0 <= u < j < v < i+1 ==> a[u] <= a[v]
    {
      if (a[j-1] > a[j]) {
        var temp:int := a[j-1];
        a[j] := temp;
        a[j-1] := a[j];
      }
      j := j - 1;
    }
    i := i+1;
  }
}

