  type Box_<T> = seq<T>
  type Box<T> = b: Box_<T> | |b| == 1 witness *
  datatype List<T> = Nil | Cons(t: T, Box<List<T>>)
  ghost function Length<T>(l: List<T>): int {
    match l {
      case Nil => 0
      case Cons(t, q) =>
        assert q[0] in q;
        var l :| l in q;
        Length(l)
    }
  }
}
