    reads a
{ 
    forall i :: (0 <= i < n) ==> forall j :: (i < j < n) ==> a[i] != a[j]
}
function sorted(a:array<int>, n:int):bool
    requires a != null
    requires 0 <= n <= a.Length
    reads a
{ 
    forall i :: (0 <= i < n) ==> forall j :: (i < j < n) ==> a[i] <= a[j]
}
function contains(b:array<int>, m:int, v:int):bool
    requires b != null
    requires 0 <= m <= b.Length 
    reads b
{
