}
method Deduplicate(a:array<int>, n:int) returns (b:array<int>, m:int)
    requires a != null
    requires 0 <= n <= a.Length
    requires sorted(a,n)
    ensures b != null
    ensures 0 <= m <= b.Length
    ensures sorted(b,m) && unique(b,m)
    ensures forall k :: (0 <= k < n) && contains(b,m,a[k]) ==> contains(a,n,a[k])
{
    b := new int[n];
    if (n == 0) {return b,0;}
    var i := 1;
    b[0] := a[0];
    m := 1;
    while ( i < n)
        decreases n - i 
        invariant 1 <= i <= n
        invariant 1 <= m <= i
        invariant sorted(b,m) && unique(b,m) 
        invariant forall k :: (0 <= k < i) && contains(b,m,a[k]) ==> contains(a,n,a[k])
    {
        if (a[i] > b[m-1]) {
            b[m] := a[i];
            m := m + 1;
        }
        i := i+1;
    }
    return b,m;
}

function P(i: nat, j: nat ) : nat
     requires j<=i;
{ if j==0 || i==j then 1 else P(i-1,j-1)+P(i-1,j)  }
ghost method LemmaSym(i: nat, j: nat )
     requires j<=i;
     ensures P(i,j)==P(i,i-j);
method CalcP(i: nat, j: nat ) returns (res:nat)
     requires j<=i;
{ if (j==0 || i==j) 
  { res:= 1; }
 else 
     { var res1 := CalcP(i-1,j-1);
       var res2 := CalcP(i-1,j);
       res:= res1 + res2;
     }      
}

