method findMax(s: seq<int>) returns(lo: int)
requires |s| > 0
ensures isMax(s, lo)
{
    assert(|s| > 0);
    assert(0 < 1 <= |s| && isMax(s[..1], 0));
    lo := 0;
    assert(0 <= lo < |s| && isMax(s[..1], lo)); 
    var hi : int := |s| - 1 ;
    assert(0 <= hi < |s| && isMax(s[hi..], 0));
    assert(hi - lo >= 0);
    while (lo < hi) 
        decreases hi - lo
        invariant 0 <= lo <= hi < |s|
        invariant (isMax2(s, 0, lo, lo) && isMax2(s, hi, |s|-1 , lo)) || (isMax2(s, 0, lo, hi) && isMax2(s, hi, |s|-1 , hi))
    {
        if(s[lo] <= s[hi])
        {
            lo := lo + 1;
        }
        else
        {
            hi := hi - 1;   
        }
    }   
}
predicate isMax2(s: seq<int>, lo: int, hi: int, max: int){
    0 <= max < |s| && 0 <= lo <= hi < |s| && forall x : int :: lo <= x <= hi ==> s[max] >= s[x]
}
predicate isMax(s : seq<int>, lo: int){
    0 <= lo < |s| && forall x: int :: 0 <= x < |s| ==> s[lo] >= s[x]
}

method isPrefix(pre: string, str: string) returns (res: bool)
  requires |pre| <= |str|
  ensures str == pre + str[|pre|..] ==> res == true
  ensures str != pre + str[|pre|..] ==> res == false
{
  if pre == str[0..|pre|]
  {
    return true;
  }
  return false;
}
method isSubstring(sub: string, str: string) returns (res: bool)
{
  if |sub| > |str| 
  {
    return false;
  }
  var prefResult := isPrefix(sub, str);
  if prefResult == true
  {
    return true;
  }
  else if(|str| >= 1) 
  {
    var subResult := isSubstring(sub, str[1..]);
    if subResult == true
    {
      return true;
    }
  }
  return false;
}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
{
  if |str1| < k || |str2| < k 
  {
    return false;
  }
  var result := isSubstring(str1[0..k], str2);
  if result == true
  {
    return true;
  }
  else if |str1| >= 1 
  {
    var result2 := haveCommonKSubstring(k, str1[1..], str2);
    if result2 == true
    {
      return true;
    }
  }
  return false;
}
method maxCommonSubstringLength(str1: string, str2: string) returns (len: nat)
{
    len := 0;
    var i := 1;
    while(i <= |str1| && i <= |str2|)
    {
      var result := haveCommonKSubstring(i, str1, str2);
      if result == true
      {
        len := i;
      }
      i := i + 1;
    }
}
