ghost predicate Has<T>(a: array<T>, x: T)
  reads a
{
  exists i :: 0 <= i < a.Length && a[i] == x
}
method SmallestMissing(a: array<nat>) returns (s: nat)
  ensures forall i :: 0 <= i < a.Length ==> a[i] != s  
  ensures forall x :: 0 <= x < s ==> Has(a, x)  
{
  var N := a.Length;  
  var e := new bool[N](_ => false);  
  forall n | 0 <= n < N && a[n] < N {
    e[a[n]] := true;
  }
  s := 0;
  while s < N && e[s]
    invariant 0 <= s <= N
    invariant forall j :: 0 <= j < s ==> Has(a, j)
  {
    s := s + 1;
  }
  if s == N {
    Lemma(a);
  }
}
lemma Lemma(a: array<nat>)
  requires forall x :: 0 <= x < a.Length ==> Has(a, x)
  ensures forall i :: 0 <= i < a.Length ==> a[i] < a.Length
{
  var ms := multiset(a[..]);
  var i, ns := 0, multiset{};
  while i < a.Length
    invariant 0 <= i <= a.Length
    invariant forall x :: x in ns ==> 0 <= x < i
    invariant |ns| == i && ns <= ms
  {
    assert Has(a, i);
    ns := ns + multiset{i};
    i := i + 1;
  }
  assert |ms - ns| == 0;
  forall i | 0 <= i < a.Length
    ensures a[i] < a.Length
  {
    assert a[i] in ms;
  }
}
method SmallestMissing_Loop(a: array<nat>) returns (s: nat)
  ensures forall i :: 0 <= i < a.Length ==> a[i] != s  
  ensures forall x :: 0 <= x < s ==> Has(a, x)  
{
  var N := a.Length;  
  var e := new bool[N](_ => false);  
  var n := 0;
  while n < N
    invariant 0 <= n <= N
    invariant forall i :: 0 <= i < n && a[i] < N ==> e[a[i]]
    invariant forall j :: 0 <= j < N && e[j] ==> Has(a, j)
  {
    if a[n] < N {
      e[a[n]] := true;
    }
    n := n + 1;
  }
  s := 0;
  while s < N && e[s]
    invariant 0 <= s <= N
    invariant forall j :: 0 <= j < s ==> Has(a, j)
  {
    s := s + 1;
  }
  if s == N {
    Lemma(a);
  }
}

predicate isPrefixPred(pre:string, str:string)
{
	(|pre| <= |str|) && pre == str[..|pre|]
}
predicate isNotPrefixPred(pre:string, str:string)
{
    !(|pre| <= |str|) || !(pre == str[..|pre|])
}
lemma PrefixNegationLemma(pre:string, str:string)
	ensures  isPrefixPred(pre,str) <==> !isNotPrefixPred(pre,str)
	ensures !isPrefixPred(pre,str) <==>  isNotPrefixPred(pre,str)
{}
predicate isSubstringPred(sub:string, str:string)
{
  exists x :: ((|sub| <= x <= |str|) && (isPrefixPred(sub,str[x..]))) 
}
predicate isNotSubstringPred(sub:string, str:string)
{
    forall x :: !((|sub| <= x <= |str|) && (isPrefixPred(sub,str[x..])))
}
lemma SubstringNegationLemma(sub:string, str:string)
	ensures  isSubstringPred(sub,str) <==> !isNotSubstringPred(sub,str)
	ensures !isSubstringPred(sub,str) <==>  isNotSubstringPred(sub,str)
{}
predicate haveCommonKSubstringPred(k:nat, str1:string, str2:string)
{
  	exists i,j :: (((j==i+k) && (k<=|str1| && k<=|str2|) && (0<=i<=|str1|-k)) && isSubstringPred(str1[i..j],str2))
}
predicate haveNotCommonKSubstringPred(k:nat, str1:string, str2:string)
{
	 forall i,j :: !(((j==i+k) && (k<=|str1| && k<=|str2|) && (0<=i<=|str1|-k)) && isSubstringPred(str1[i..j],str2))
}
lemma commonKSubstringLemma(k:nat, str1:string, str2:string)
	ensures  haveCommonKSubstringPred(k,str1,str2) <==> !haveNotCommonKSubstringPred(k,str1,str2)
	ensures !haveCommonKSubstringPred(k,str1,str2) <==> haveNotCommonKSubstringPred(k,str1,str2)
{}

method best_time_to_buy_and_sell_stock(prices: array<int>) returns (max_profit: int)
    requires 1 <= prices.Length <= 100000
    requires forall i :: 0 <= i < prices.Length ==> 0 <= prices[i] <= 10000
    ensures forall i, j :: 0 <= i < j < prices.Length ==> max_profit >= prices[j] - prices[i]
{
    var min_price := 10001;
    max_profit := 0;
    var i := 0;
    while (i < prices.Length)
        invariant 0 <= i <= prices.Length
        invariant forall j :: 0 <= j < i ==> min_price <= prices[j]
        invariant forall j, k :: 0 <= j < k < i ==> max_profit >= prices[k] - prices[j]
    {
        var price := prices[i];
        if (price < min_price)
        {
            min_price := price;
        }
        if (price - min_price > max_profit) {
            max_profit := price - min_price;
        }
        i := i + 1;
    }
}

