function fib(n : nat ) : nat
  decreases n
{
    if n < 2 then n else fib(n - 2) + fib(n - 1)
}
method computeFib (n : nat) returns (x : nat)
  ensures x == fib(n)
{
    var i := 0;
    x := 0;
    var y := 1;
    assert x == fib(i) && y == fib(i+1) && 0 <= i <= n; 
    while  i < n 
      decreases n - i 
      invariant x == fib(i) && y == fib(i+1) && 0 <= i <= n 
    {
      ghost var V0 := n - i;
      assert i < n && x == fib(i) && y == fib(i+1) && 0 <= i <= n 
             && n - i == V0; 
        x, y := y, x + y; 
        i := i + 1;
      assert x == fib(i) && y == fib(i+1) && 0 <= i <= n 
             && 0 <= n-i < V0; 
    }
    assert i >= n && x == fib(i) && y == fib(i+1) && 0 <= i <= n;
}

method exercise2(a:int, n:int) returns (x:int)
ensures x >= n && (x == 2*a - n || x == 3*n - 2*a + 1)
{
    x := 3*n - 2*a + 1;
    assert (x > n || x <= n) && x == 3*n - 2*a + 1;
    if (x <= n) {
