        assert 2*a >= 2*n+1 && x == 3*n - 2*a + 1;
        assert a > n && x == 3*n - 2*a + 1;
        x := 2*a-n;
        assert a > n && x == 2*a - n;
        assert 2*a > 2*n && x+n == 2*a;
        assert x+n > 2*n && x+n == 2*a;
        assert x > n && x == 2*a - n;
