class Learner<T(==)>
{
  var majority: int; 
  var current:  int; 
  var accepted: map<T, seq<int>>; 
  constructor ()
    modifies this;
  {
    majority := 0;
    current  := -1;
  }
  method Learn(id: int, round: int, value: T) returns (learned: bool, ret: T)
    modifies this;
  {
    if round > current {
      current := round;
      accepted := map[]; 
    }
    if round == current {
      if value !in accepted {
        accepted := accepted[ value := [id] ];
      } else {
        accepted := accepted[ value := accepted[value] + [id] ];
      }
      if |accepted[value]| >= majority {
        return true, value;
      }
    }
    return false, value;
  }
  method Configure(num_acceptors: int)
    modifies this;
  {
    majority := num_acceptors/2 + 1;
  }
}

function gcd(m: nat, n: nat): nat
  requires m>=1;
  requires n>=1;
  decreases m+n;
{ 
  if (n==m) then n 
  else if (m>n) then gcd(m-n, n) 
  else gcd(m, n-m) 
}
method GcdCal(m: nat, n: nat) returns (res: nat)
  requires m>=1;
  requires n>=1;
  ensures res == gcd(m, n);
{ 
  var m1: nat, n1: nat := m, n;
  while (m1!=n1) 
    invariant m1 >= 1;
    invariant n1 >= 1;
    invariant gcd(m1, n1) == gcd (m, n)
    decreases m1+n1
  {  
    if (m1>n1) { 
      m1 := m1-n1; 
    } 
    else { 
      n1 := n1-m1; 
    }
 }
 return m1;
}

method isPrefix(pre: string, str: string) returns (res:bool)
{
 var i := 0;
