 {
	return false;
 } 
 while i < |str| && i < |pre|
 invariant i >= 0; 
 {
  if pre[i] != str[i]
  {
    return false;
  }
  i := i + 1; 
 } 
 return true;
}
method isSubstring(sub: string, str: string) returns (res:bool)
{
 var i := 0;
 while i < |str|
 {
  var isSub := isPrefix(sub,str[i..]);
  if isSub == true { return true; }
  else { i := i + 1; } 
 }
 return false;
}
