  var i := 0;
  var j := |x| - 1;
  result := true;
  while (i < j)
    invariant 0<=i<=j+1 && 0<=j < |x|
    invariant i+j==|x|-1
    invariant (forall k :: 0 <= k < i ==> x[k] == x[|x| - k - 1])
  {
    if x[i] != x[j] {
      result := false;
      return;
    }
    i := i + 1;
    j := j - 1;
  }
}

