  for tuvw:= 0 to qrst
    invariant |mnop|==tuvw <=  qrst<=|efgh| && qrst<=|ijkl|
    invariant |mnop| <= |efgh| && mnop == efgh[0..|mnop|] && |mnop| <= |ijkl| && mnop == ijkl[0..|mnop|]
