method ComputeFact(n : nat) returns (res : nat)
requires n > 0;
ensures res == fact(n);
{
	res := 1;
	var i := 2;
	while (i <= n)
	invariant (i <= n + 1) && res == fact(i - 1);
	decreases (n - i + 1);
	{
		res := res * i;
		i := i + 1;
	}
}
function fact(m : nat) : nat
requires m > 0;
{
	if (m == 1) then 1 else m * fact(m - 1)
}

method findMax (a : array<int>, n : int) returns (r:int)
requires a.Length > 0;
requires 0 < n <= a.Length;
requires a != null;
ensures 0 <= r < n && forall k :: 0 <= k < n ==> a[r] >= a[k];
{
  var mi;
  var i;
  mi := 0;
  i := 0;
  while (i < n)
    invariant 0 <= i <= n;
    invariant 0 <= mi < n;
    invariant forall k :: 0 <= k < i ==> a[mi] >= a[k];
    decreases n - i;
  {
    if (a[i] > a[mi])
    { mi := i; }
    i := i + 1;
  }
  return mi;
}

method isPalindrome(chars: array<int>) returns (r: bool)
  requires chars != null
  ensures r <==> forall x: int :: 0 <= x < chars.Length
                 ==> chars[x] == chars[chars.Length - (x + 1)]
{
    var i: nat := 0;
    var j: nat := chars.Length;
    while i < j
      invariant i + j == chars.Length && i >= 0
      invariant forall k: int :: 0 <= k < i
                ==> chars[k] == chars[chars.Length - (k + 1)]
    {
        j := j - 1;
        if chars[i] != chars[j]
        {
            return false;
        }
        i := i + 1;
    }
    return true;
}

