  invariant forall j :: 0 <= j < |s2| ==> !s2[j].ordered &&
    (!s2[j].suitablity || cd > s2[j].use_by_date);
  decreases |s1| - i;
  {
    if !s1[i].ordered && (!s1[i].suitablity 
      || cd > s1[i].use_by_date) {
