datatype Colour = RED | BLUE | YELLOW | GREEN 
method FlagSort(flag:array<Colour>) 
    modifies flag;
    ensures forall x::  0 < x < flag.Length ==> 
        (flag[x] == RED ==> flag[x-1] == RED) 
    ensures forall x::  0 < x < flag.Length ==> 
        (flag[x] == BLUE ==> 
            (flag[x-1] == BLUE || flag[x-1] == RED )) 
    ensures forall x::  0 < x < flag.Length ==> 
        (flag[x] == YELLOW ==> 
            (
                flag[x-1] == BLUE || 
                flag[x-1] == RED  ||
                flag[x-1] == YELLOW 
            )
        ) 
    ensures forall x::  0 < x < flag.Length ==> 
        (flag[x] == GREEN ==> (
            flag[x-1] == BLUE   ||
            flag[x-1] == RED    || 
            flag[x-1] == YELLOW || 
            flag[x-1] == GREEN
        )) 
{
    var next := 0;
    var blue := 0;
    var yellow := flag.Length; 
    var green := flag.Length; 
    while (next != yellow) 
        decreases yellow + green - next
        invariant 0 <= blue <= next <= yellow <= green <= flag.Length
        invariant forall x:: 0      <= x < blue        ==> flag[x] == RED
        invariant forall x:: blue   <= x < next        ==> flag[x] == BLUE
        invariant forall x:: yellow <= x < green       ==> flag[x] == YELLOW
        invariant forall x:: green  <= x < flag.Length ==> flag[x] == GREEN
    {
        match (flag[next])
        {
            case BLUE => 
                next := next + 1; 
            case RED => 
                flag[blue], flag[next] := flag[next], flag[blue];
                blue := blue + 1;
                next := next + 1;
            case YELLOW => 
                yellow := yellow -1;
                flag[yellow] , flag[next] := flag[next], flag[yellow];
            case GREEN => 
                green := green -1;
                yellow := yellow -1;
                flag[green] , flag[next] := flag[next], flag[green];
                if ( yellow != green){
                    flag[next], flag[yellow]:= flag[yellow],flag[next];
                }
        }
    }
}

predicate positive(s:seq<int>)
{forall u::0<=u<|s| ==> s[u]>=0}
method mfirstNegative(v:array<int>) returns (b:bool, i:int)
ensures b <==> exists k::0<=k<v.Length && v[k]<0
ensures b ==> 0<=i<v.Length && v[i]<0 && positive(v[0..i])
{ 
 i:=0;
 b:=false;
 while (i<v.Length && !b)
    invariant 0<=i<=v.Length
    invariant b <==> exists k::0<=k<i && v[k]<0
    invariant b ==> v[i-1]<0 && positive(v[0..i-1])
    decreases v.Length - i
  { 
    b:=(v[i]<0);
    i:=i+1;
   }
  if (b){i:=i-1;}
}
method mfirstNegative2(v:array<int>) returns (b:bool, i:int)
ensures b <==> exists k::0<=k<v.Length && v[k]<0
ensures b ==> 0<=i<v.Length && v[i]<0 && positive(v[0..i])
{ 
 i:=0;b:=false;
 while (i<v.Length && !b)
    invariant 0<=i<=v.Length
    invariant b ==> i<v.Length && v[i]<0 && !(exists k::0<=k<i && v[k]<0)
    invariant b <== exists k::0<=k<i && v[k]<0
    decreases v.Length - i - (if b then 1 else 0)
  { 
    b:=(v[i]<0);
    if (!b) {i:=i+1;}
   }
}

