predicate perm(A:seq<int>, B:seq<int>)
{
	multiset(A) == multiset(B)
}
predicate partOrdered(A:array<int>, a:int, b:int)
	requires 0 <= a <= b <= A.Length
	reads A
{
	forall i,j :: a <= i < j < b ==> A[i] <= A[j]
}
predicate ordered(A:array<int>)
	reads A
{
	partOrdered(A, 0, A.Length)
}
method selectionSort (A:array<int>)
	modifies A
	ensures ordered(A)                          
	ensures perm (A[..], old(A[..]))            
{
	if A.Length > 1
	{
		var i, j := 0, 0;
		var min_idx := i;
		while i < A.Length
		invariant 0 <= i <= A.Length
		invariant 0 <= min_idx < A.Length
		invariant perm (A[..], old(A[..]))          
		invariant forall k, l :: 0 <= k < i <= l < A.Length ==> A[k] <= A[l]        
		invariant partOrdered(A, 0, i)              
		{
			j := i + 1;
			min_idx := i;
			while j < A.Length
			invariant 1 <= j <= A.Length
			invariant i <= min_idx < A.Length
			invariant perm (A[..], old(A[..]))              
			invariant forall k :: i <= k < j ==> A[k] >= A[min_idx]
			{
				if A[j] < A[min_idx]
				{
					min_idx := j;
				}
				j := j + 1;
			}
			A[i], A[min_idx] := A[min_idx], A[i];
			i := i + 1;
		}
	}
}
method Main() {
	var A := new int[10];
	A[0],A[1],A[2],A[3],A[4],A[5],A[6],A[7],A[8],A[9] := 4,8,8,3,5,10,9,9,4,7;
	print "A = ", A[..], "\n";
	selectionSort(A);
	print "A = ", A[..], "\n";
}

module Commons {
predicate sorted(a: array<int>)
   reads a
{
   forall j, k :: 0 <= j < k < a.Length ==> a[j] <= a[k]
}
method Copy(a: array<int>) returns (b: array<int>) 
    ensures multiset(a[..]) ==  multiset(b[..])
    ensures a.Length == b.Length
{ 
    b := new int[a.Length];
    var i := 0;
    while i < a.Length 
        decreases a.Length - i
        invariant a.Length == b.Length
        invariant 0 <= i <= a.Length
        invariant forall k :: 0 <= k < i ==> a[k] == b[k]
    {
        b[i] := a[i];
        i := i + 1;
    }
    assert a[..] == b[..];
}
method Swap(a: array<int>, i: int, j: int) 
    modifies a
    requires 0 <= i < a.Length
    requires 0 <= j < a.Length
    ensures forall x :: 0 <= x < a.Length && x != i && x!= j ==> a[x] == old(a[x])
    ensures a[j] == old(a[i]) && a[i] == old(a[j])
{
    var ai, aj := a[i], a[j];
    a[i] := aj;
    a[j] := ai;
}
}

lemma MergeLShift(v: bv128, i: nat, j: nat)
  requires i <= 128 && j <= 128 && i + j <= 128
