method IncDec(x: int, y: int) returns (sum: int)
    ensures sum == x + y
{
    sum:=x;
    var tmp := y;
    while tmp != 0
    invariant sum + tmp == x + y;
    decreases Abs(tmp);
    {
        if(tmp > 0){
            sum := sum + 1;
            tmp := tmp - 1;
        }
        else if(tmp < 0){
            sum := sum -1;
            tmp := tmp +1;
        }
    }
}
