function More(x: int): int {
  if x <= 0 then 1 else More(x - 2) + 3
}
lemma {:induction false} Increasing(x: int)
  ensures x < More(x)
{
  if x <= 0 {
    calc {
      x;
    <=  { assert x <= 0; }
      0;
    <  
      1;
    ==  
      More(x);
    }
  } else {
    calc {
      More(x);
    ==  
      More(x - 2) + 3;
    >  { Increasing(x - 2); }
      x - 2 + 3;
    >  
      x;
    }
  }
}

method random(a: int, b: int) returns (r: int)
  ensures a <= b ==> a <= r <= b
lemma eqMultiset_t<T>(t: T, s1: seq<T>, s2: seq<T>)
  requires multiset(s1) == multiset(s2)
  ensures t in s1 <==> t in s2
{
  calc <==> {
    t in s1;
    t in multiset(s1);
  }
}
lemma eqMultiset<T>(s1: seq<T>, s2: seq<T>)
  requires multiset(s1) == multiset(s2)
  ensures forall t :: t in s1 <==> t in s2
{
  forall t {
    eqMultiset_t(t, s1, s2);
  }
}
method swap<T>(a: array<T>, i: int, j: int)
  requires 0 <= i < a.Length && 0 <= j < a.Length
  modifies a
  ensures a[i] == old(a[j])
  ensures a[j] == old(a[i])
  ensures forall m :: 0 <= m < a.Length && m != i && m != j ==> a[m] == old(a[m])
  ensures multiset(a[..]) == old(multiset(a[..]))
{
  var t := a[i];
  a[i] := a[j];
  a[j] := t;
}
method getAllShuffledDataEntries<T(0)>(m_dataEntries: array<T>) returns (result: array<T>)
  ensures result.Length == m_dataEntries.Length
  ensures multiset(result[..]) == multiset(m_dataEntries[..])
{
  result := new T[m_dataEntries.Length];
  forall i | 0 <= i < m_dataEntries.Length {
    result[i] := m_dataEntries[i];
  }
  assert result[..] == m_dataEntries[..];
  var k := result.Length - 1;
  while (k >= 0)
    invariant multiset(result[..]) == multiset(m_dataEntries[..])
  {
    var i := random(0, k);
    assert i >= 0 && i <= k;
    if (i != k) {
      swap(result, i, k);
    }
    k := k - 1;
  }
}
function set_of_seq<T>(s: seq<T>): set<T>
{
  set x: T | x in s :: x
}
lemma in_set_of_seq<T>(x: T, s: seq<T>)
  ensures x in s <==> x in set_of_seq(s)
lemma subset_set_of_seq<T>(s1: seq<T>, s2: seq<T>)
  requires set_of_seq(s1) <= set_of_seq(s2)
  ensures forall x :: x in s1 ==> x in s2
method getRandomDataEntry<T(==)>(m_workList: array<T>, avoidSet: seq<T>) returns (e: T)
  requires m_workList.Length > 0
{
  var k := m_workList.Length - 1;
  while (k >= 0)
  {
    var i := random(0, k);
    assert i >= 0 && i <= k;
    e := m_workList[i];
    if (e !in avoidSet) {
      return e;
    }
    k := k - 1;
  }
  return m_workList[0];
}

method add_by_one (x:int, y:int) returns (r:int)
  requires y >= 0;
  ensures r == x + y;
{
  var i:int := 0;
  r := x;
  while (i < y)
    invariant i <= y;
    invariant r == x + i;
    decreases y-i;
  {
    r := r + 1;
    i := i + 1;
  }
  return r;
}
method bar (x:int, y:int) returns (r:int)
  requires y >= 0;
  ensures r == x + y;
{
  var i := 0;
  r := x;
  assert (i <= y && r == x + i);
  assert (y-i >= 0);
  i, r := *, *;
  assume (i <= y && r == x + i);
  assume (y-i >= 0);
  ghost var rank_before := y-i;
  if (i < y)
  {
    r := r + 1;
    i := i + 1;
    assert (i <= y && r == x + i);
    assert (y-i >= 0);
    assert (rank_before - (y-i) > 0);
    assume (false);
  }
  return r;
}

module GT {
    trait Node {}
    trait Edge {
        var from: Node;
        var to: Node;
    }
    trait Graph {
        var nodes: set<Node>;
        var edges: set<Edge>;
        var paths: set<seq<Edge>>;
    }
    class WeightedDAG extends Graph {
        constructor(nodes: set<Node>, edges: set<WeightedEdge>, paths: set<seq<WeightedEdge>>)
        requires forall i :: i in edges ==> i.from in nodes && i.to in nodes;
        {
            this.nodes := nodes;
            this.edges := edges;
            this.paths := paths;
        }
    }
    class WeightedEdge extends Edge {
        var weight :nat;
        constructor(from: Node, to: Node, weight: nat) {
            this.from := from;
            this.to := to;
            this.weight := weight;
        }
    }
    predicate isPath(edges: set<WeightedEdge>, subEdges: seq<WeightedEdge>) 
    reads subEdges
    requires forall i :: i in subEdges ==> i in edges
    {
        forall i :: 0 <= i < |subEdges|-1 ==>
                subEdges[i].to == subEdges[i+1].from
    }
}

datatype Result<T> = Failure(msg: string) | Success(value: T) {
  predicate IsFailure() { Failure? }
  function PropagateFailure(): Result<T> requires IsFailure() { this }
  function Extract(): (t: T) requires !IsFailure() ensures t == this.value { this.value }
}
