method Max(a:array<int>) returns (m:int)
    requires 0 < a.Length
    ensures forall k : int :: 0 <= k < a.Length ==> a[k] <= m
{
    m := a[0];
    assert forall k : int :: 0 <= k < 1 ==> a[k] <= m;
    var i := 1;
    while i < a.Length
        decreases a.Length-i 
        invariant 1 <= i <= a.Length
        invariant forall k : int :: 0 <= k < i ==> a[k] <= m
    {
        if m < a[i]
        { m := a[i];} 
        i := i + 1;
    }
    assert forall k : int :: 0 <= k < a.Length ==> a[k] <= m;
}
function fib(n:int):int 
    requires n >= 0
    decreases n
{
    if n == 0 then 1
    else if n == 1 then 1
    else fib(n-1)+fib(n-2)
}
method Fib(n:int) returns (f:int)
    requires n >= 0
    ensures f == fib(n)
{
    if n == 0
    {f:=1;}
    else
    {
        var a := 1;
        var b := 1;
        var i := 1;
        while i < n
            decreases n - i
            invariant 1 <= i <= n
            invariant a == fib(i-1)
            invariant b == fib(i)
        {
            a, b := b, a+b;
            i := i + 1;
        }
        f := b;
    }
}

predicate member(n: int, s: seq<int>) {
    exists i :: 0 <= i < |s| && s[i] == n
}
predicate notmember(n: int, s: seq<int>) {
    forall i :: 0 <= i < |s| ==> s[i] != n
}
method isMember(m: int, s: seq<int>) returns (ismember: bool)
    ensures ismember ==> member(m, s)
    ensures !ismember ==> notmember(m, s)
{
    ismember := false;
    var i := 0;
    assert notmember(m, s[..i]); 
    while (i < |s| && !ismember) 
        decreases |s| - i + (if ismember then 0 else 1)
        invariant 0 <= i <= |s|
        invariant notmember(m, s[..i])
        invariant ismember ==> i < |s|  
        invariant (ismember ==> member(m, s[..i+1]))
    {
        assert notmember(m, s[..i]);
        assert (i < |s| && !ismember);
        assert (s[i] == m) ==> notmember(m, s[..i]);
        assert !(s[i] == m) ==> notmember(m, s[..i+1]);  
        assert (s[i] == m) ==> member(m, s[..i+1]);
        if s[i] == m {
            assert member(m , s[..i+1]);
            assert true ==> member(m, s[..i+1]);
            assert notmember(m, s[..i]); 
            ismember := true;
            assert ismember ==> member(m, s[..i+1]);
            assert notmember(m, s[..i]);
        } else {
            assert ismember ==> member(m, s[..i]);
            assert notmember(m, s[..i+1]);  
            i := i + 1;
            assert ismember ==> member(m, s[..i]);
            assert notmember(m, s[..i]);
        }
        assert ismember ==> member(m, s[..i+1]);
        assert notmember(m, s[..i]);
    }
    assert notmember(m, s[..i]) && !(i < |s| && !ismember); 
    assert ismember ==> member(m, s[..i+1]) && !(i < |s| && !ismember);
}

function fib(n : nat ) : nat
  decreases n
  {
    if n < 2 then n else fib(n - 2) + fib(n - 1)
  }
method computeFib (n : nat) returns (x : nat)
ensures fib(n) == x
{
  var i := 0;
  x := 0;
  var y := 1;
  while  i < n
    decreases n - i
    invariant x == fib(i)
    invariant y == fib(i + 1)
    invariant i <= n
  {
    x, y := y, x + y; 
    i := i + 1;
  }
}
