{
    var r, w, b := 0, 0, a.Length;
    while w < b 
        decreases b - w
        invariant 0 <= r <= w <= b <= a.Length
        invariant forall i :: 0 <= i < r ==> a[i] == Red
        invariant forall i :: r <= i < w ==> a[i] == White
        invariant forall i :: b <= i < a.Length ==> a[i] == Blue
        invariant multiset(a[..]) == multiset(old(a[..])) 
    {
        match a[w]
