ghost method isCelebrityP(n: int, i: int) returns (r : bool)
requires n >= 0 && 0 <= i < n;
ensures r <==> isCelebrity(n, i);
{
    var j := 0;
    r := true;
    while j < n
    decreases n - j;
    invariant 0 <= j <= n;
    invariant r ==> forall k :: 0 <= k < j && k != i ==> knows(k, i) && !knows(i, k);
    {
        if j != i {
            if !knows(j, i) || knows(i, j) {
