datatype pc_label = x_pc | y_pc | done
class TeachingConcurrency {
    var pc: array<pc_label>;
    var x: array<int>;
    var y: array<int>;
    method Init()
    requires pc.Length == x.Length == y.Length;
    requires forall i : int :: 0 <= i < pc.Length ==> pc[i] == x_pc;
    requires x != y
    requires 1 < pc.Length; 
    ensures IndInv();
    modifies pc;
    {
        assert pc[1] != done;
    }
    method Proc(i: int)
    requires 0 <= i < pc.Length;
    requires IndInv();
    ensures IndInv();
    modifies x, y, pc;
    {
        x[i] := 1;
        pc[i] := y_pc;
        Yield(i);
        y[i] := x[(i - 1) % x.Length];
        pc[i] := done;
        assert x_Inv();
        assert AllDone() ==> x[(i - 1) % x.Length] == 1;
        assert AllDone() ==> y[i] == 1;
        Yield(i);
    }
    predicate AllDone()
    reads `pc, pc;
    {
        forall i : int :: 0 <= i < pc.Length ==> pc[i] == done
    }
    predicate Safety()
    requires pc.Length == y.Length;
    reads `pc, pc, `y, y;
    {
        AllDone() ==> exists i : int :: 0 <= i < y.Length && y[i] == 1
    }
    predicate x_Inv()
    requires pc.Length == x.Length;
    reads `pc, pc, `x, x;
    {
        forall i : int ::
            (0 <= i < pc.Length && pc[i] in {y_pc, done}) ==>
            x[i] == 1
    }
    predicate IndInv()
    reads `pc, pc, `x, x, `y, y;
    {
        x != y && pc.Length == x.Length == y.Length &&
        Safety() && x_Inv()
    }
    method Yield(i: int)
    requires 0 <= i < pc.Length;
    requires IndInv();
    ensures IndInv();
    ensures pc[i] == old(pc[i]);
    modifies pc, x, y;
}

