method Test(z: int) {
  assume forall x: int, y: int :: P(x, y) == P(y, x);
  assume forall x: int, y: int :: P(x, y) == (P(y, x));
  assume forall x: int, y: int :: P(x, y) == P(x, y+1);
  assert forall x :: true || Q(x) || Q(0);
  assert forall x :: true || Q(x) || Q(z);
  assert forall x :: true || P(x, 1) || P(x, z);
  assert forall x :: true || Q(x) || Q(0+1);
  assert forall x :: true || Q(x) || Q(if z > 1 then z else 3 * z + 1);
  assert forall x :: true || Q(x) || Q(if z > 1 then x else 3 * z + 1);
  assert forall x :: true || Q(x) || (var xx := x+1; Q(xx));
}

method IndexOf(a:array<char>, n:int, K:char)
    returns (pos:int)
    requires 0 <= n <= a.Length
    ensures pos == -1 ==> forall j : int :: 0 <= j < n ==> a[j] != K    
    ensures 0 <= pos < n ==> a[pos] == K                                
    ensures -1 <= pos < n
{
    var i := 0;
    pos := -1;
    while i < n
        decreases n-i               
        invariant 0 <= i <= n       
        invariant -1 <= pos < i     
        invariant 0 <= pos < i ==> a[pos] == K
        invariant pos == -1 ==> forall j : int :: 0 <= j < i ==> a[j] != K    
    {
        if a[i] == K
        {
            pos := i;
        }
        i := i + 1;
    }
}
method Fillk(A:array<char>, n:int, K:char) returns (s:bool)
    requires 0 <= n <= A.Length
    ensures s <==> forall j : int :: 0 <= j < n ==> A[j] == K          
{
    var i := 0;
    s := true;
    while i < n
        decreases n-i
        invariant 0 <= i <= n
        invariant  s  <==> forall j : int :: 0 <= j < i ==> A[j] == K
    {
        if A[i] != K 
        { s := false; }
        i := i + 1;
    }
} 
method FillkUsingExists(A:array<char>, n:int, K:char) returns (s:bool)
    requires 0 <= n <= A.Length
    ensures !s <==> exists j : int :: 0 <= j < n && A[j] != K            
{
    var i := 0;
    s := true;
    while i < n
        decreases n-i
        invariant 0 <= i <= n
        invariant !s <==> exists j : int :: 0 <= j < i && A[j] != K
    {
        if A[i] != K 
        { s := false; }
        i := i + 1;
    }
} 
method Fillk2(A:array<char>, n:int, K:char) returns (s:bool)
    requires 0 <= n <= A.Length
    ensures s <==> forall j : int :: 0 <= j < n ==> A[j] == K          
{
    var i := 0;
    s := true;
    while i < n
        decreases n-i
        invariant 0 <= i <= n
        invariant  s  <==> forall j : int :: 0 <= j < i ==> A[j] == K
    {
        if A[i] != K 
        { return false; }
        i := i + 1;
    }
    return true;
} 
method Reverse(a:array<int>, n:int) returns (b:array<int>)
    requires 0 <= n <= a.Length
    ensures a.Length == b.Length
    ensures forall k : int :: 0 <= k < n ==> b[k] == a[n-1-k]
    ensures forall k : int :: n <= k < a.Length ==> b[k] == a[k]
{
    b := new int[a.Length];
    var i := 0;
    while i < n
        decreases n-i
        invariant 0 <= i <= n
        invariant forall k : int :: 0 <= k < i ==> b[k] == a[n-1-k]
    {
        b[i] := a[n-1-i];
        i := i + 1;
    }
    while i < a.Length
        decreases a.Length - i
        invariant n <= i <= a.Length
        invariant forall k : int :: 0 <= k < n ==> b[k] == a[n-1-k]
        invariant forall k : int :: n <= k < i ==> b[k] == a[k]
    {
        b[i] := a[i];
        i := i + 1;
    }
}

datatype Tree<T> = Leaf | Node(Tree<T>, Tree<T>, T)
datatype List<T> = Nil | Cons(T, List<T>)
function flatten<T>(tree:Tree<T>):List<T>
{
	match tree
    case Leaf => Nil
    case Node(l_tree,r_tree,v) => Cons(v,append(flatten(l_tree),flatten(r_tree)))
}
function append<T>(xs:List<T>, ys:List<T>):List<T>
{
	match xs
    case Nil => ys
    case Cons(x,xs') => Cons(x, append(xs',ys))
}
function treeContains<T(==)>(tree:Tree<T>, element:T):bool
{
    match tree
    case Leaf => false
    case Node(l_tree, r_tree, v) => treeContains(l_tree,element) || treeContains(r_tree,element) || (v == element) 
}
function listContains<T(==)>(xs:List<T>, element:T):bool
{
	match xs
    case Nil => false
    case Cons(x,xs') => (x == element) || listContains(xs', element)
}
lemma memberOfAppend<T>(e:T, xs:List<T>, ys:List<T>)
ensures listContains(append(xs,ys),e) == ( listContains(xs,e)  ||  listContains(ys,e) )
{
    match xs
    case Nil => {}
    case Cons(x,xs') => {
        calc {
           listContains(append(xs,ys),e);
            == listContains(append(Cons(x,xs'),ys),e);
            == listContains(Cons(x,append(xs',ys)),e);
            == (e==x) || listContains(append(xs',ys),e);
            == { memberOfAppend(e,xs',ys); }
               (e==x) || listContains(xs',e) || listContains(ys,e);
            == listContains(Cons(x,xs'),e)  || listContains(ys,e);
            == listContains(xs,e)           || listContains(ys,e);
        }
    }
}
lemma sameElements<T>(tree:Tree<T>, element:T)
ensures treeContains(tree, element) <==> listContains(flatten(tree), element)
{
    match tree
    case Leaf => {}
    case Node (l_tree,r_tree,v) => {
        calc{treeContains(tree,element);
        == treeContains(Node (l_tree,r_tree,v),element);
        == treeContains(l_tree,element) || treeContains(r_tree,element) || (v==element);
        == {sameElements(l_tree,element);
           sameElements(r_tree,element);}
        listContains(flatten(l_tree),element) || listContains(flatten(r_tree),element) || (v==element);
        =={memberOfAppend(element,flatten(l_tree),flatten(r_tree));} 
        listContains(append(flatten(l_tree),flatten(r_tree)),element) || (v==element);
        ==listContains(Cons(v,append(flatten(l_tree),flatten(r_tree))),element);
        ==listContains(flatten(tree), element);
        }      
    }  
}

