method Square (a: int) returns (x: int)
	requires a >= 1
	ensures x == a * a
{
	x := 1;
	var y := 1;
	while y < a
		invariant 1 <= y <= a
		invariant x == y*y
	{
		y := y + 1;
		x := x + 2*y - 1;
	}
}
method Square2 (a: int) returns (x: int)
	requires a >= 1
	ensures x == a * a
{
	x := 1;
	var y := 1;
	while y < a
		invariant 1 <= y <= a
		invariant x == sumSerie(y)
	{
		y := y + 1;
		x := x + 2*y - 1;
	}
	assert 1 <= y <= a && x == sumSerie(y) && y >= a;
	assert y == a && x == sumSerie(y);
	assert x == sumSerie(a);
	sumSerie_Lemma(a);
	assert x == a * a;
}
function sumSerie (n: int): int
	requires n >= 1
{
	if n == 1 then 1 else sumSerie(n-1) + 2*n - 1
}
lemma sumSerie_Lemma(n: int)
	requires n >= 1
	ensures sumSerie(n) == n*n
{}
function power(b: int, e: nat): int
{
	if e == 0 then 1 else b* power(b, e-1)
}
method compute_power(b: int, e: nat) returns (p: int)
	ensures p == power(b, e)
{
	var t, x := e, b;
	p := 1;
	while t > 0
		invariant 0 <= t <= e
		invariant p * power(x, t) == power(b, e)
	{
		if t % 2 == 0 {
			even_Lemma(x, t);
			x, t := x*x, t/2;
		}
		else {
			p, t := p*x, t-1;
		}
	}
}
lemma even_Lemma (b: int, e: nat)
	requires e % 2 == 0
	ensures power(b, e) == power(b*b, e/2)
{
	if e > 0 {
		even_Lemma(b, e-2);
		assert power(b, e) == b*b * power(b, e-2) == b*b * power(b*b, (e/2-1)) == power(b*b, e/2);
	}
}

predicate valid_base(b : nat) {
  b >= 2
}
predicate nitness(b : nat, n : nat)
  requires (valid_base(b))
{
  0 <= n < b
}
method nit_increment(b : nat, n : nat) returns (sum : nat, carry : nat)
  requires (valid_base(b))
  requires (nitness(b, n))
  ensures (nitness(b, sum))
  ensures (nitness(b, carry))
{
  sum := (n + 1) % b;
  carry := (n + 1) / b;
}
predicate is_max_nit(b : nat, q : nat) {
  q == b - 1
}
method max_nit(b: nat) returns (nmax : nat)
  requires (valid_base(b))
  ensures (nitness(b, nmax))
  ensures (is_max_nit(b, nmax))
{
  nmax := b - 1;
}
method nit_flip(b: nat, n : nat) returns (nf : nat)
  requires (valid_base(b))
  requires (nitness(b, n))
  ensures (nitness (b, nf))
{
  var mn : nat := max_nit(b);
  assert 0 < n < b ==> n <= b - 1;
  assert 0 == n ==> n <= b - 1;
  assert n <= b - 1;
  assert mn == b - 1;
  assert 0 <= n <= mn;
  nf := mn - n;
}
method nit_add(b : nat, x : nat, y : nat) returns (z : nat, carry : nat)
  requires (valid_base(b))
  requires (nitness(b, x))
  requires (nitness(b, y))
  ensures  (nitness(b, z))
  ensures  (nitness(b, carry))
  ensures  (carry == 0 || carry == 1)
{
  z := (x + y) % b;
  carry := (x + y) / b;
  assert x + y < b + b;
  assert (x + y) / b < (b + b) / b;
  assert (x + y) / b < 2;
  assert carry < 2;
  assert carry == 0 || carry == 1;
}
method nit_add_three(b : nat, c : nat, x : nat, y : nat) returns (z : nat, carry : nat)
  requires (valid_base(b))
  requires (c == 0 || c == 1)
  requires (nitness(b, x))
  requires (nitness(b, y))
  ensures  (nitness(b, z))
  ensures  (nitness(b, carry))
  ensures  (carry == 0 || carry == 1)
{
  if(c == 0) {
    z, carry := nit_add(b, x, y);
  } else {
    z := (x + y + 1) % b;
    carry := (x + y + 1) / b;
    assert 0 <= b - 1;
    assert 0 <= x < b;
    assert 0 == x || 0 < x;
    assert 0 < x ==> x <= b - 1;
    assert 0 <= x <= b - 1;
    assert 0 <= y < b;
    assert 0 == y || 0 < y;
    assert 0 <= b - 1;
    assert 0 < y ==> y <= b - 1;
    assert 0 <= y <= b - 1;
    assert x + y <= (b - 1) + (b - 1);
    assert x + y <= 2 * b - 2;
    assert x + y + 1 <= 2 * b - 2 + 1;
    assert x + y + 1 <= 2 * b - 1;
    assert 2 * b - 1 < 2 * b;
    assert x + y + 1 < 2 * b;
    assert (x + y + 1) / b < 2;
    assert (x + y + 1) / b == 0 || (x + y + 1) / b == 1;
  }
}
predicate bibble(b : nat, a : seq<nat>)
{
  valid_base(b) && 
  |a| == 4 && 
  forall n :: n in a ==> nitness(b, n)
}
method bibble_add(b : nat, p : seq<nat>, q : seq<nat>) returns (r : seq<nat>)
  requires (valid_base(b))
  requires (bibble(b, p))
  requires (bibble(b, q))
  ensures  (bibble(b, r))
{
  var z3, c3 := nit_add(b, p[3], q[3]);
  var z2, c2 := nit_add_three(b, c3, p[2], q[2]);
  var z1, c1 := nit_add_three(b, c2, p[1], q[1]);
  var z0, c0 := nit_add_three(b, c1, p[0], q[0]);
  r := [z0, z1, z2, z3];
}
method bibble_increment(b : nat, p : seq<nat>) returns (r : seq<nat>)
  requires (valid_base(b))
  requires (bibble(b, p))
  ensures  (bibble(b, r))
{
  var q : seq<nat> := [0, 0, 0, 1];
  assert bibble(b, q);
  r := bibble_add(b, p, q);
}
method bibble_flip(b : nat, p : seq<nat>) returns (fp : seq<nat>)
  requires (valid_base(b))
  requires (bibble(b, p))
  ensures  (bibble(b, fp))
{
  var n0 := nit_flip(b, p[0]);
  var n1 := nit_flip(b, p[1]);
  var n2 := nit_flip(b, p[2]);
  var n3 := nit_flip(b, p[3]);
  fp := [n0, n1, n2, n3];
}
method n_complement(b : nat, p : seq<nat>) returns (com : seq<nat>)
  requires (valid_base(b))
  requires (bibble(b, p))
  ensures  (bibble(b, com))
{
  var fp := bibble_flip(b, p);
  var fpi := bibble_increment(b, fp);
  com := fpi;
}
method Main() {
  var b := 3;
  var bibble1 := [2, 1, 0, 2];
  var complement := n_complement(b, bibble1);
  var bibble_sum := bibble_add(b, bibble1, complement);
  print bibble1, " + ", complement, " = ", bibble_sum, " (should be [0, 0, 0, 0])\n";
}

method QuickSort(a: array<int>, start: int, end: int)
   requires a.Length >= 1;
   requires 0 <= start <= end <= a.Length; 
   requires 0 <= start <= end < a.Length ==> 
    forall j :: start <= j < end ==> a[j] < a[end];
   requires 0 < start <= end <= a.Length ==>
    forall j :: start <= j < end ==> a[start - 1] <= a[j];
   modifies a;
   ensures sorted(a, start, end);
   ensures forall j :: 0 <= j < start || end <= j < a.Length ==> old(a[j]) == a[j];
   ensures 0 <= start <= end < a.Length ==>
    forall j :: start <= j < end ==> a[j] < a[end];
   ensures 0 < start <= end <= a.Length ==>
    forall j :: start <= j < end ==> a[start - 1] <= a[j];
   decreases end - start;
{
   if(end - start > 1)
   {
     var pivot := partition(a, start, end);
     QuickSort(a, start, pivot);
     QuickSort(a, pivot + 1, end);
   } else {
     return;
   }
}
predicate sorted (a: array<int> ,low: int , high :int )
  requires 0 <= low <= high <= a.Length;
  reads a;
{
  forall j,k ::low <= j < k < high ==> a[j] <= a[k]
}
method partition(a: array<int>, start: int, end: int) returns (pivot: int)
   requires a.Length > 0;
   requires 0 <= start < end <= a.Length;
   requires 0 <= start <= end < a.Length ==>
    forall j :: start <= j < end ==> a[j] < a[end];
   requires 0 < start <= end <= a.Length ==>
    forall j :: start <= j < end ==> a[start - 1] <= a[j];
   modifies a;
   ensures 0 <= start <= pivot < end <= a.Length;
   ensures forall j :: start <= j < pivot ==> a[j] < a[pivot];
   ensures forall j :: pivot < j < end ==> a[pivot] <= a[j];
   ensures forall j :: 0 <= j < start || end <= j < a.Length ==> old(a[j]) == a[j];
   ensures 0 <= start <= end < a.Length ==>
    forall j :: start <= j < end ==> a[j] < a[end];
   ensures 0 < start <= end <= a.Length ==>
    forall j :: start <= j < end ==> a[start - 1] <= a[j];
{
    pivot := start;
    var index := start + 1; 
      while(index < end)
         invariant start <= pivot < index <= end;
         invariant forall j :: start <= j < pivot ==> a[j] < a[pivot];
         invariant forall j :: pivot < j < index ==> a[pivot] <= a[j];
         invariant forall j :: 0 <= j < start || end <= j < a.Length ==>
            old(a[j]) == a[j];
         invariant 0 <= start <= end < a.Length ==> 
            forall j :: start <= j < end ==> a[j] < a[end];
         invariant 0 < start <= end <= a.Length ==> 
            forall j :: start <= j < end ==> a[start - 1] <= a[j];
      {
         if(a[index] < a[pivot]) 
         {
          assert 0 < start <= end <= a.Length ==> 
          forall j :: start <= j < end ==> a[start - 1] <= a[j];
 	var counter := index - 1;
 	var temp := a[index];
 	a[index] := a[counter];
 	while(counter > pivot)
 	   invariant forall j :: start <= j < pivot ==> a[j] < a[pivot];
 	   invariant forall j :: pivot < j < index + 1 ==> a[pivot] <= a[j];
 	   invariant a[pivot] > temp;
	   invariant forall j :: 0 <= j < start || end <= j < a.Length ==> old(a[j]) == a[j];
 	   invariant 0 <= start <= end < a.Length ==> 
                forall j :: start <= j < end ==> a[j] < a[end];
     	   invariant 0 < start <= end <= a.Length ==> 
                forall j :: start <= j < end ==> a[start - 1] <= a[j];
	{
 	   a[counter + 1] := a[counter];
 	   counter := counter - 1;
 	}
 	a[pivot + 1] := a[pivot];
 	pivot := pivot + 1;
 	a[pivot - 1] := temp;
       }
       index := index + 1;
     }
}
