class Stack {
  var a: array<int>;
  var size:int;
  function isEmpty():bool 
    reads `size
  { size == 0 }
  function RepInv():bool
    reads `size, `a
  {
    0 < a.Length && 0 <= size <= a.Length
  }
  constructor() 
    ensures RepInv()
  {
    a := new int[1];
    size := 0;
  }
  method push(x:int) 
    requires RepInv()
    ensures !isEmpty() && RepInv()
    modifies `a, `size, a
  {
    if( size >= a.Length ) { grow(); }
    a[size] := x;
    size := size + 1;
  }
  method pop() returns (x:int)
    requires RepInv() && !isEmpty()
    requires RepInv()
    modifies `size
  {
    size := size - 1;
    return a[size];
  }
  method grow() 
    requires RepInv()
    ensures RepInv()
    ensures size < a.Length
    ensures forall k:int :: 0 <= k < size ==> a[k] == old(a[k])
    ensures fresh(a)
    modifies `a
  {
    var b := new int[a.Length*2];
    var i := 0;
    while i < size 
      decreases size - i 
      invariant 0 <= i <= size
      invariant forall k :: 0 <= k < i ==> b[k] == a[k]
      modifies b
    {
      b[i] := a[i];
      i := i + 1;
    }
    a := b;
  }
}

function More(x: int): int {
  if x <= 0 then 1 else More(x - 2) + 3
}
lemma {:induction false} Increasing(x: int)
  ensures x < More(x)
{
  if x <= 0 {
    assert More(x) == 1; 
  } else {
    assert More(x) == More(x - 2) + 3; 
    Increasing(x - 2); 
    assert x - 2 < More(x - 2); 
    assert x + 1 < More(x - 2) + 3; 
    assert x + 1 < More(x); 
  }
}

function Ackermann (m: nat, n : nat) : nat
decreases m,n;
{
	if m == 0 then
	n + 1
	else if n == 0 then
	Ackermann (m - 1, 1)
	else
	Ackermann (m - 1, Ackermann(m, n-1))
}
