    var id1 := binarySearch(a, 6);
    assert a[3] == 6; 
    assert id1 == 3;
    var id2 := binarySearch(a, 3);
    assert id2 == -1; 
    var id3 := binarySearch(a, 4);
    assert a[1] == 4 && a[2] == 4; 
    assert id3 in {1, 2};
}

