method step(phil: array<int>, fork: array<int>, n:int, i: int)
  requires phil.Length==fork.Length==n
  requires n > 2
  requires 0<=i<n
  requires fork!=phil
  requires forall j:: 0<=j<n ==> inv(phil[j], fork[j], fork[(j+1)%n])
  ensures forall j:: 0<=j<n ==>  inv(phil[j], fork[j], fork[(j+1)%n])
  modifies phil
  modifies fork
{
  if(phil[i]==0)
  {
    phil[i] := 1;
  }
  else if (phil[i]==1)
  {
    if (fork[i]==1 && fork[(i+1) % n] ==2)
    {
      phil[i] := 2;
    }
    else
    {
      var temp := pick(2);
      if(temp==0)
      {
        if(fork[i]==0)
        {
          fork[i] := 1;
        }
      }
      else
      {
        if(fork[(i+1)%n]==0)
        {
          fork[(i+1)%n] := 2;
        }
      }
    }
  }
  else
  {
    assert forall j {:trigger phil[j]}:: 0<=j<n ==> inv(phil[j], fork[j], fork[(j+1)%n]);
    notEqMod(i,n);
    fork[i] := 0;
    fork[(i+1)%n] := 0;
    phil[i] := 0;
  }
}
method Main(phil: array<int>, fork:array<int>, n:int)
  modifies phil
  modifies fork
  requires fork!=phil
  requires phil.Length==fork.Length==n
  requires n>2
  requires forall j:: 0<=j<n ==> phil[j] == 0
  requires forall j:: 0<=j<n ==> fork[j] == 0
{
  var steps := pick(1000);
  while(steps>0)
    decreases steps
    invariant forall j:: 0<=j<n ==> inv(phil[j], fork[j], fork[(j+1)%n])
  {
    steps := steps - 1;
    var i := pick(n);
    step(phil, fork, n, i);
  }
}

method Reverse(a: seq<int>) returns (r: seq<int>)
  ensures |a| == |r|
  ensures forall i :: 0 <= i < |a| ==> r[i] == a[|a| - i - 1]
{
  var remainder := a;
  r := [];
  while (|remainder| > 1)
    invariant |remainder| == |a| - |r|
    invariant forall i :: 0 <= i < |r| ==> r[i] == a[|r| - i - 1]
    invariant forall i :: 0 <= i < |remainder| ==> remainder[i] == a[|r| + i]
  {
    r := [remainder[0]] + r;
    remainder := remainder[1..];
  }
  r := remainder + r;
}
