function Sum(s: set<int>): int {
  if s == {} then 0 else
    var x := Pick(s);
    x + Sum(s - {x})
} by method {
  var sum := 0;
  var s' := s;
  while s' != {}
    invariant s' <= s
    invariant sum + Sum(s') == Sum(s)
  {
    var x :| x in s';
    var s'' := s' - {x};
    assert s'' + {x} == s';
    SumLemma(s'', x);
    sum, s' := sum + x, s'';
  }
  return sum;
}
