lemma SumLemma(s: set<int>, y: int)
  requires y !in s
  ensures Sum(s + {y}) == Sum(s) + y
{
  if s == {} {
  } else {
    var sy := s + {y};
    assert s == sy - {y};
    var x := Pick(sy);
    if x == y {
    } else {
      var s'x := s - {x};
      assert s'x + {x} == s;
      calc {
        Sum(s + {y});
      ==
        Sum(sy);
      ==  
        x + Sum(sy - {x});
      ==  { assert sy - {x} == s'x + {y}; }
        x + Sum(s'x + {y});
      ==  { SumLemma(s'x, y); }
        x + Sum(s'x) + y;
      ==  { SumLemma(s'x, x); }
        Sum(s'x + {x}) + y;
      ==
        Sum(s) + y;
      }
    }
  }
}

